INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81027, 'Edoxaban', 'Oxaprozin', 'Major', 'Concomitant use of edoxaban with other agents that alter hemostasis such as aspirin, nonsteroidal anti-inflammatory drugs (NSAIDs), platelet aggregation inhibitors, other anticoagulants, thrombolytic agents, or drugs that cause thrombocytopenia may increase the risk of bleeding.', NULL, 'Caution is recommended if edoxaban must be used with other agents that alter hemostasis.', NULL, 'Caution is recommended if edoxaban must be used with other agents that alter hemostasis. Patients should be monitored for increased anticoagulant effects and bleeding complications. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits. Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole', 'Protein C, Acenocoumarol, Selexipag', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81128/', '[1] "Product Information. Savaysa (edoxaban)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81028, 'Efavirenz', 'Oxaprozin', 'Moderate', 'Coadministration of efavirenz with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Efavirenz has been associated with hepatotoxicity during postmarketing use.', NULL, 'The risk of hepatic injury should be considered when efavirenz is used in combination with other agents that are potentially hepatotoxic (e.', NULL, 'The risk of hepatic injury should be considered when efavirenz is used in combination with other agents that are potentially hepatotoxic (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; other HIV reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of liver function tests should occur before and during treatment, especially in patients with underlying hepatic disease (including hepatitis B or C coinfection) or marked transaminase elevations.', 'Synergism', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Misoprostol, Chondroitin sulfate, Esomeprazole, Glucosamine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81129/', '[1] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[2] Elsharkawy AM, Schwab U, McCarron B, et al. "Efavirenz induced acute liver failure requiring liver transplantation in a slow drug metaboliser." J Clin Virol 58 (2013): 331-3[3] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[4] "Product Information. Retrovir (zidovudine)." Glaxo Wellcome, Research Triangle Park, NC.[5] Lotterer E, Ruhnke M, Trautman M, et al "Decreased and variable systemic availability of zidovudine in patients with AIDS if administered with a meal." Eur J Clin Pharmacol 40 (1991): 305-8[6] Unadkat JD, Collier AC, Crosby SS, et al "Pharmacokinetics of oral zidovudine (azidothymidine) in patients with AIDS when administered with and without a high-fat meal." AIDS 4 (1990): 229-32[7] Sahai J, Gallicano K, Garber G, et al. "The effect of a protein meal on zidovudine pharmacokinetics in HIV-infected patients." Br J Clin Pharmacol 33 (1992): 657-60', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81029, 'Enalapril', 'Oxaprozin', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of ACE inhibitors. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and ACE inhibitors may also cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function. Acute renal failure may occur, although effects are usually reversible. Chronic use of NSAIDs alone may be associated with renal toxicities, including elevations in serum creatinine and BUN, tubular necrosis, glomerulitis, renal papillary necrosis, acute interstitial nephritis, nephrotic syndrome, and renal failure.', NULL, 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Chondroitin sulfate, Esomeprazole, Glucosamine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81130/', '[1] Townend JN, Doran J, Lote CJ, Davies MK "Peripheral haemodynamic effects of inhibition of prostaglandin synthesis in congestive heart failure and interactions with captopril." Br Heart J 73 (1995): 434-41[2] Ahmad S "Indomethacin-enalapril interaction: an alert." South Med J 84 (1991): 411-2[3] Silberbauer K, Stanek B, Templ H "Acute hypotensive effect of captopril in man modified by prostaglandin synthesis inhibition." Br J Clin Pharmacol 14 (1982): s87-93[4] "Product Information. Toradol (ketorolac)." Roche Laboratories, Nutley, NJ.[5] Morgan T, Anderson A "Interaction of indomethacin with felodipine and enalapril." J Hypertens 11 (1993): S338-9[6] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs: a randomized, double-blind, placebo-controlled trial of ibuprofen compared with acetaminophen." Ann Intern Med 107 (1987): 628-35[7] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.[8] Abdel-Haq B, Magagna A, Favilla S, Salvetti A "Hemodynamic and humoral interactions between perindopril and indomethacin in essential hypertensive subjects." J Cardiovasc Pharmacol 18 (1991): s33-6[9] "Product Information. Daypro (oxaprozin)." Searle, Skokie, IL.[10] Polonia J, Boaventura I, Gama G, Camoes I, Bernardo F, Andrade P, Nunes JP, Brandao F, Cerqueiragomes M "Influence of non-steroidal anti-inflammatory drugs on renal function and 24h ambulatory blood pressure-reducing effects of enalapril and nifedipine gastrointestinal therapeutic system in hypertensive patients." J Hypertens 13 (1995): 925-31[11] Seto S, Aoi W, Iwami K, et al "Effect of propranolol and indomethacin on the depressor action of captopril in patients with essential hypertension." Clin Exp Hypertens 9 (1987): 623-7[12] Allon M, Pasque CB, Rodriguez M "Interaction of captopril and ibuprofen on glomerular and tubular function in humans." Am J Physiol 259 (1990): f233-8[13] Moore TJ, Crantz FR, Hollenberg NK "Contribution of prostaglandins to the antihypertensive action of captopril in essential hypertension." Hypertension 3 (1981): 168-73[14] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[15] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[16] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81030, 'Enoxacin', 'Oxaprozin', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', 'Rabeprazole, Phenylbutazone, Misoprostol, Glucosamine, Chondroitin sulfate, Esomeprazole', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81131/', '[1] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[2] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.[3] Davey PG "Overview of drug interactions with the quinolones." J Antimicrob Chemother 22(suppl c) (1988): 97-107[4] Domagala JM "Structure-activity and structure-side-effect relationships for the quinolone antibacterials." J Antimicrob Chemother 33 (1994): 685-706[5] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[6] "Product Information. Floxin (ofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[7] "Product Information. Tequin (gatifloxacin)" Bristol-Myers Squibb, Princeton, NJ.[8] Segev S. Rehavi M, Rubinstein E "Quinolones, theophylline, and diclofenac interactions with the gamma-aminobutyric acid receptor." Antimicrob Agents Chemother 32 (1988): 1624-6[9] Hooper DC, Wolfson JS "The fluoroquinolones: pharmacology, clinical uses, and toxicities in humans." Antimicrob Agents Chemother 28 (1985): 716-21[10] Ball P "Ciprofloxacin: an overview of adverse experiences." J Antimicrob Chemother 18 (1986): 187-93[11] "Product Information. Penetrex (enoxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[12] "Product Information. Raxar (grepafloxacin)." Glaxo Wellcome, Research Triangle Park, NC.[13] Ball P, Tillotson G "Tolerability of fluoroquinolone antibiotics: past, present and future." Drug Saf 13 (1996): 343-8[14] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81031, 'Enoxaparin', 'Oxaprozin', 'Major', 'In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during low molecular weight heparin (LMWH) or heparinoid therapy may be increased by the concomitant use of other drugs that affect coagulation, including nonsteroidal anti-inflammatory drugs (NSAIDs). The development of epidural and spinal hematoma can lead to long-term or permanent paralysis. Theoretically, NSAIDs may potentiate the risk of bleeding complications associated with LMWH or heparinoid therapy. NSAIDs interfere with platelet adhesion and aggregation and may prolong bleeding time in healthy individuals. While these effects are generally slight and of relatively short duration with most NSAIDs (except aspirin) at recommended dosages, they may be of pronounced clinical significance when combined with the inhibitory effects of LMWHs or heparinoids on the clotting cascade.', NULL, 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids.', NULL, 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits.', 'Synergism', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole', 'Protein C, Acenocoumarol, Selexipag', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81132/', '[1] Weale AE, Warwick DJ, Durant N, Prothero D "Is there a clinical interaction between low molecular weight heparin and non-steroidal analgesics after total hip replacement?" Ann R Coll Surg Engl 77 (1995): 35-7[2] "Product Information. Orgaran (danaparoid)." Organon, West Orange, NJ.[3] "Product Information. Lovenox (enoxaparin)." Rhone-Poulenc Rorer, Collegeville, PA.[4] "Product Information. Innohep (tinzaparin)" DuPont Pharmaceuticals, Wilmington, DE.[5] "Product Information. Normiflo (ardeparin)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[6] Walker AM "Predictors of bleeding during heparin therapy." JAMA 244 (1980): 1209-12[7] Klinkhardt U, Breddin HK, Esslinger HU, Haas S, Kalatzis A, Harder S "Interaction between the LMWH reviparin and aspirin in healthy volunteers." Br J Clin Pharmacol 49 (2000): 337-41[8] Heiden D, Rodvien R, Mielke CH "Heparin bleeding, platelet dysfunction, and aspirin." JAMA 246 (1981): 330-1[9] Theroux P, Ouimet H, McCans J, et al. "Aspirin, heparin, or both to treat acute unstable angina." N Engl J Med 319 (1988): 1105-6[10] Price AJ, Frcpath DO "Is there a clinical interaction between low molecular weight heparin and non-steroidal analgesics after total hip replacement?" Ann R Coll Surg Engl 77 (1995): 395[11] "Product Information. Fragmin (dalteparin)." Pharmacia and Upjohn, Kalamazoo, MI.[12] Bang CJ, Riedel B, Talstad I, Berstad A "Interaction between heparin and acetylsalicylic acid on gastric mucosal and skin bleeding in humans." Scand J Gastroenterol 27 (1992): 489-94', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81032, 'Entecavir', 'Oxaprozin', 'Moderate', 'Because entecavir is primarily eliminated by renal excretion, coadministration with drugs that affect renal function may alter the plasma concentrations of entecavir and/or the coadministered drug. In patients with renal impairment, the apparent oral clearance of entecavir has been shown to decrease as the creatinine clearance decreases.', NULL, 'Caution is advised if entecavir is prescribed in combination with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if entecavir is prescribed in combination with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during therapy with entecavir.', 'Excretion', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81133/', '[1] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81033, 'Eplerenone', 'Oxaprozin', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effect of eplerenone. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. In patients with renal impairment receiving these antihypertensive agents, concomitant use of NSAIDs has also resulted in severe hyperkalemia.', NULL, 'Patients receiving eplerenone who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving eplerenone who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Finerenone', 'Misoprostol, Chondroitin sulfate, Esomeprazole, Glucosamine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81134/', '[1] Abdel-Haq B, Magagna A, Favilla S, Salvetti A "Hemodynamic and humoral interactions between perindopril and indomethacin in essential hypertensive subjects." J Cardiovasc Pharmacol 18 (1991): s33-6[2] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs: a randomized, double-blind, placebo-cont