INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81165, 'Ramucirumab', 'Oxaprozin', 'Major', 'Theoretical concerns exist that nonsteroid anti-inflammatory drugs (NSAIDs) and salicylates may potentiate the risk of bleeding and gastrointestinal perforation associated with ramucirumab therapy. Ramucirumab can cause bleeding, including severe and sometimes fatal hemorrhagic complications.', NULL, 'Caution is advised if ramucirumab must be used with chronic NSAID or salicylate therapy.', NULL, 'Caution is advised if ramucirumab must be used with chronic NSAID or salicylate therapy. Close clinical and laboratory observation for bleeding complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools. Ramucirumab should be permanently discontinued in patients who experience severe bleeding or gastrointestinal perforation.', 'Synergism', 'Rabeprazole, Misoprostol, Glucosamine, Chondroitin sulfate, Rofecoxib, Esomeprazole, Celecoxib, Valdecoxib', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Amivantamab, Atezolizumab, Pembrolizumab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81266/', '[1] "Product Information. Cyramza (ramucirumab)." Eli Lilly and Company, Indianapolis, IN.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81166, 'Ranitidine (bismuth citrate)', 'Oxaprozin', 'Minor', 'H2 antagonists may alter the disposition of nonsteroidal anti-inflammatory drugs (NSAIDs), resulting in increased or decreased plasma concentrations. Data are varied, even for the same NSAID. The mechanism may be related to inhibition of metabolism, changes in gastric pH that decrease absorption, and/or reduced urinary elimination. Statistically significant changes have been small and of limited clinical significance. Clinical monitoring of patient response and tolerance is recommended.', NULL, '-', NULL, '-', 'Others', 'Rabeprazole, Phenylbutazone, Misoprostol, Indomethacin, Meloxicam, Glucosamine, Chondroitin sulfate, Rofecoxib, Esomeprazole, Celecoxib, Valdecoxib', NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81267/', '[1] Said SA, Foda AM "Influence of cimetidine on the pharmacokinetics of piroxicam in rat and man." Arzneimittelforschung 39 (1989): 790-2[2] "Product Information. Daypro (oxaprozin)." Searle, Skokie, IL.[3] Scavone JM, Greenblatt DJ, Matlis R, Harmatz JS "Interaction of oxaprozin with acetaminophen, cimetidine, and ranitidine." Eur J Clin Pharmacol 31 (1986): 371-4', 1767369485);
