INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81372, 'Eliglustat', 'Deferasirox', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat is also a substrate of P-gp efflux transporter.', NULL, 'Patients receiving eliglustat with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to eliglustat.', NULL, 'Patients receiving eliglustat with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to eliglustat. Concomitant use should be avoided if an interaction is suspected.', 'Metabolism', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Deferoxamine, Levoleucovorin, Arginine, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81473/', '[1] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[2] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81373, 'Encorafenib', 'Deferasirox', 'Major', 'Coadministration with drugs that have known ulcerogenic or hemorrhagic potential may increase the risk of gastrointestinal bleeding associated with the use of deferasirox. Fatal gastrointestinal hemorrhages have been reported during deferasirox therapy, especially in elderly patients with advanced hematologic malignancies and/or low platelet counts.', NULL, 'Caution is advised if deferasirox is prescribed with drugs that have known ulcerogenic or hemorrhagic potential, including but not limited to NSAIDs, salicylates, corticosteroids, oral bisphosphonates, platelet inhibitors, anticoagulants, thrombolytic agents, or agents that commonly cause thrombocytopenia.', NULL, 'Caution is advised if deferasirox is prescribed with drugs that have known ulcerogenic or hemorrhagic potential, including but not limited to NSAIDs, salicylates, corticosteroids, oral bisphosphonates, platelet inhibitors, anticoagulants, thrombolytic agents, or agents that commonly cause thrombocytopenia. Patients should be advised to contact their physician if they develop potential signs and symptoms of gastrointestinal injury such as abdominal pain, bloating, dizziness, lightheadedness, hematemesis, anorexia, and/or black, tarry stools.', 'Synergism', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Deferoxamine, Levoleucovorin, Arginine, More', 'Futibatinib, Nilotinib, Lenvatinib, Pralsetinib, Dabrafenib, Asciminib, Vandetanib, Neratinib, Afatinib, Erdafitinib, Trametinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81474/', '[1