INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1372594, 'Brimonidine (topical)', 'Lemborexant', 'Moderate', 'Topically administered alpha-2 adrenergic receptor agonists such as apraclonidine and brimonidine are systemically absorbed, with the potential for producing rare but clinically significant systemic effects. Although the interaction has not been specifically studied, the possibility of an additive or potentiating effect with central nervous system (CNS) depressants such as alcohol, barbiturates, opiates, anxiolytics, sedatives, and anesthetics should be considered.', 'DDInter', 1767317475, 'Patients receiving topical alpha-2 adrenergic receptor agonists in combination with agents that can cause CNS depression should be made aware of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness and confusion, and counseled to avoid activities requiring mental alertness. Patients should also avoid rising abruptly from a sitting or recumbent position and notify their physician if they experience orthostasis or tachycardia.', 'Synergism', 'Patients receiving topical alpha-2 adrenergic receptor agonists in combination with agents that can cause CNS depression should be made aware of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness and confusion, and counseled to avoid activities requiring mental alertness.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1372595, 'Brimonidine (topical)', 'Levamlodipine', 'Moderate', 'Topically administered alpha-2 adrenergic receptor agonists such as apraclonidine and brimonidine are systemically absorbed, with the potential for producing rare but clinically significant systemic effects such as hypotension and bradycardia. The possibility for an additive or potentiating effect on blood pressure and heart rate should be considered when used with other medications that affect these parameters, such as ophthalmic and systemic beta blockers, vasodilators, cardiac glycosides, and antihypertensive agents.', 'DDInter', 1767317475, 'Blood pressure and pulse rate should be monitored regularly when topical alpha-2 adrenergic receptor agonists are prescribed in combination with cardiovascular drugs. Patients should be advised to notify their physician if they experience slow pulse, irregular heartbeat, dizziness, lightheadedness, or syncope.', 'Synergism', 'Blood pressure and pulse rate should be monitored regularly when topical alpha-2 adrenergic receptor agonists are prescribed in combination with cardiovascular drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
