INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1372686, 'Brimonidine (topical)', 'Procarbazine', 'Moderate', 'Topically administered alpha-2 adrenergic receptor agonists such as brimonidine are systemically absorbed, with the potential for producing rare but clinically significant systemic effects. Despite relative alpha-2 selectivity, theoretical concerns exist that coadministration with monoamine oxidase inhibitors (MAOIs) may increase the risk of hypertension due to potentiation of alpha-1 stimulation, which produces vasoconstriction.', 'DDInter', 1767317475, 'Patients receiving brimonidine in combination with MAOIs should be made aware of the potential for increased adverse effects, and counseled to avoid activities requiring mental alertness until they know how these agents affect them. Patients should also avoid rising abruptly from a sitting or recumbent position and notify their physician if they experience orthostasis or tachycardia. Blood pressure should be monitored closely.', 'Synergism', 'Patients receiving brimonidine in combination with MAOIs should be made aware of the potential for increased adverse effects, and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', NULL, NULL, 'Moderate', 'pharmacodynamic');
