INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1372770, 'Brinzolamide (ophthalmic)', 'Bismuth subsalicylate', 'Major', 'The combination of large doses of salicylates and oral carbonic anhydrase inhibitors (CAI) may rarely result in severe metabolic acidosis and/or salicylate toxicity. The mechanism is unknown but may involve salicylate-induced displacement of CAIs from plasma protein-binding sites and reduced renal clearance, or CAI-induced plasma pH changes resulting in increased amounts of unionised salicylates entering the CNS.', 'DDInter', 1767317475, 'In general, concomitant use of antirheumatic doses of salicylates and oral CAIs is not recommended. If coadministration is necessary, careful monitoring of the patient''s mental status and acid base balance is strongly recommended. Patients should be advised to promptly notify their physicians if they experience symptoms such as lethargy, tinnitus, confusion, nausea, vomiting, or hyperventilation.', 'Distribution', 'In general, concomitant use of antirheumatic doses of salicylates and oral CAIs is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1372771, 'Choline salicylate', 'Brinzolamide (ophthalmic)', 'Major', 'The combination of large doses of salicylates and oral carbonic anhydrase inhibitors (CAI) may rarely result in severe metabolic acidosis and/or salicylate toxicity. The mechanism is unknown but may involve salicylate-induced displacement of CAIs from plasma protein-binding sites and reduced renal clearance, or CAI-induced plasma pH changes resulting in increased amounts of unionised salicylates entering the CNS.', 'DDInter', 1767317475, 'In general, concomitant use of antirheumatic doses of salicylates and oral CAIs is not recommended. If coadministration is necessary, careful monitoring of the patient''s mental status and acid base balance is strongly recommended. Patients should be advised to promptly notify their physicians if they experience symptoms such as lethargy, tinnitus, confusion, nausea, vomiting, or hyperventilation.', 'Distribution', 'In general, concomitant use of antirheumatic doses of salicylates and oral CAIs is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
