INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81531, 'Deferasirox', 'Palbociclib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of palbociclib, which is a substrate of the isoenzyme.', NULL, 'The potential for diminished therapeutic effects of palbociclib should be considered when prescribed with inducers of CYP450 3A4.', NULL, 'The potential for diminished therapeutic effects of palbociclib should be considered when prescribed with inducers of CYP450 3A4. Pharmacologic effects of palbociclib should be monitored more closely whenever an inducer is added to or withdrawn from therapy, and alternative treatment considered if an interaction is suspected.', 'Metabolism', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Promethazine, Deferoxamine, Levoleucovorin, More', 'Futibatinib, Nilotinib, Lenvatinib, Pralsetinib, Dabrafenib, Asciminib, Vandetanib, Neratinib, Afatinib, Erdafitinib, Trametinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81632/', '[1] "Product Information. Ibrance (palbociclib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Ibrance (palbociclib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81532, 'Deferasirox', 'Pamidronic acid', 'Major', 'Coadministration with oral bisphosphonates may increase the risk of gastrointestinal bleeding associated with the use of deferasirox. Fatal gastrointestinal hemorrhages have been reported during deferasirox therapy, especially in elderly patients with advanced hematologic malignancies and/or low platelet counts. Non-fatal upper gastrointestinal irritation, ulceration, and hemorrhage have also been reported, including in children and adolescents. Theoretical concerns exist regarding the potential for increased risk of renal impairment during coadministration of deferasirox with other nephrotoxic agents, including intravenous bisphosphonates.', NULL, 'Caution is advised if deferasirox is used in combination with drugs that have known ulcerogenic potential, including oral bisphosphonates.', NULL, 'Caution is advised if deferasirox is used in combination with drugs that have known ulcerogenic potential, including oral bisphosphonates. Patients should be advised to contact their physician if they develop potential signs and symptoms of gastrointestinal injury such as abdominal pain, bloating, dizziness, lightheadedness, vomiting blood, anorexia, and/or black, tarry stools. Caution is advised if deferasirox is used in combination with other potentially nephrotoxic agents, including intravenous bisphosphonates. Serum creatinine and/or creatinine clearance should be closely monitored (e.g., prior to initiation of deferasirox therapy, then weekly during the first month after initiation or modification of therapy and monthly thereafter), especially in the elderly and patients with preexisting renal impairment, comorbid conditions, dehydration, or severe infections. Dosage reduction, interruption, or discontinuation should be considered in the presence of creatinine elevations. A progressive increase in serum creatinine beyond the age-appropriate upper limit of normal may warrant an interruption of therapy. Once the creatinine has returned to within the normal range, therapy may be reinitiated at a lower dose followed by a gradual dose escalation according to the product labeling, provided the clinical benefit is expected to outweigh potential risks.', 'Synergism', 'Vosoritide, Denosumab, Cholecalciferol, Burosumab, Romosozumab', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81633/', '[1] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Rafat C, Fakhouri F, Ribeil JA, Delarue R, Le Quintrec M "Fanconi Syndrome Due to Deferasirox." Am J Kidney Dis (2009):[3] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
