INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81537, 'Deferasirox', 'Peginterferon beta-1a', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Interferon alfa-n1, Oprelvekin, Sipuleucel-T, Elapegademase, Peginterferon alfa-2a, Interferon alfacon-1, Sargramostim, Histamine, Aldesleukin, Pegademase, Pegfilgrastim, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81638/', '[1] "Product Information. Plegridy (peginterferon beta-1a)." Biogen Idec Inc, Cambridge, MA.[2] "Product Information. Betaseron (interferon beta-1b)." Berlex, Richmond, CA.[3] "Product Information. Rebif (interferon beta-1a)." Serono Laboratories Inc, Norwell, MA.[4] "Product Information. Avonex (interferon beta-1a)." Biogen, Cambridge, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81538, 'Deferasirox', 'Pemetrexed', 'Moderate', 'Coadministration with drugs that are nephrotoxic may delay and/or decrease the clearance of pemetrexed, which is primarily eliminated unchanged by the kidney via glomerular filtration and active tubular secretion.', NULL, 'Caution is advised if pemetrexed is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if pemetrexed is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). The potential for increased toxicity of pemetrexed such as bone marrow suppression should be considered. Renal function should be closely monitored during therapy.', 'Excretion', 'Fluorouracil, Mercaptopurine, Cladribine, Trifluridine, Floxuridine, Gemcitabine, Nelarabine, Cytarabine, Pralatrexate, Fludarabine, Decitabine, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81639/', '[1] "Product Information. Alimta (pemetrexed)." Lilly, Eli and Company, Indianapolis, IN.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81539, 'Deferasirox', 'Pemigatinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of pemigatinib, which is primarily metabolized by the isoenzyme in vitro.', NULL, 'The potential for diminished pharmacologic effects of pemigatinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, 'The potential for diminished pharmacologic effects of pemigatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Promethazine, Deferoxamine, Levoleucovorin, More', 'Futibatinib, Nilotinib, Lenvatinib, Pralsetinib, Dabrafenib, Asciminib, Vandetanib, Neratinib, Afatinib, Erdafitinib, Trametinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81640/', '[1] "Product Information. Pemazyre (pemigatinib)." Incyte Corporation, Wilmington, DE.[2] "Product Information. Pemazyre (pemigatinib)." Incyte Corporation, Wilmington, DE.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81540, 'Deferasirox', 'Pentamidine', 'Major', 'Theoretical concerns exist regarding the potential for increased risk of renal impairment during coadministration of deferasirox with other nephrotoxic agents. The use of deferasirox has been associated with postmarketing reports of acute renal failure, in some cases resulting in dialysis or even fatality.', NULL, 'Caution is advised if deferasirox is used in combination with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised if deferasirox is used in combination with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Serum creatinine and/or creatinine clearance should be closely monitored (e.g., prior to initiation of deferasirox therapy, then weekly during the first month after initiation or modification of therapy and monthly thereafter), especially in the elderly and patients with preexisting renal impairment, comorbid conditions, dehydration, or severe infections. Dosage reduction, interruption, or discontinuation should be considered in the presence of creatinine elevations. A progressive increase in serum creatinine beyond the age-appropriate upper limit of normal may warrant an interruption of therapy. Once the creatinine has returned to within the normal range, therapy may be reinitiated at a lower dose followed by a gradual dose escalation according to the product labeling, provided the clinical benefit is expected to outweigh potential risks.', 'Synergism', 'Miltefosine, Fexinidazole, Nifurtimox, Benznidazole', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81641/', '[1] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Rafat C, Fakhouri F, Ribeil JA, Delarue R, Le Quintrec M "Fanconi Syndrome Due to Deferasirox." Am J Kidney Dis (2009):[3] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81541, 'Deferasirox', 'Pentoxifylline', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of pentoxifylline and its active hydroxy metabolite (M1), both of which are substrates of the isoenzyme.', NULL, 'Pharmacologic response to pentoxifylline should be monitored more closely whenever a potent or moderate CYP450 1A2 inhibitor is added to or withdrawn from therapy, and the pentoxifylline dosage adjusted as necessary.', NULL, 'Pharmacologic response to pentoxifylline should be monitored more closely whenever a potent or moderate CYP450 1A2 inhibitor is added to or withdrawn from therapy, and the pentoxifylline dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience serious adverse effects such as bleeding, angina, or arrhythmias.', 'Metabolism', 'Cyclandelate, Tolazoline, Phenoxybenzamine, Phentolamine, Isoxsuprine, Niacin, Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Reslizumab, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81642/', '[1] "Product Information. Trental (pentoxifylline)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Magnusson M, Bergstrand IC, Bjorkman S, Heijl A, Roth B, Hoglund P "A placebo-controlled study of retinal blood flow changes by pentoxifylline and metabolites in humans." Br J Clin Pharmacol 61 (2006): 138-47', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81542, 'Deferasirox', 'Pexidartinib', 'Major', 'Serious cases of hepatotoxicity, some fatal, have occurred in patients treated with pexidartinib. Concomitant use of other potentially hepatotoxic agents may potentiate the risk of liver injury. The mechanism of hepatotoxicity is unknown, its occurrence cannot be predicted, and it is unknown whether liver injury occurs in the absence of increased transaminases.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided. Patients treated with pexidartinib should have liver function tests, including AST, ALT, total bilirubin, direct bilirubin, ALP, and gamma-glutamyl transferase (GGT), prior to initiation of pexidartinib, weekly for the first 8 weeks, every 2 weeks for the next month, and every 3 months thereafter. Pexidartinib therapy may require a dosage reduction, to be withheld, or permanently discontinued based on the severity of the hepatotoxicity. A recurrence of increased serum transaminases, bilirubin, or ALP may occur upon rechallenge with a reduced dose of pexidartinib. Liver function tests should be performed weekly for the first month after rechallenge. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Promethazine, Deferoxamine, Levoleucovorin, More', 'Futibatinib, Nilotinib, Lenvatinib, Pralsetinib, Dabrafenib, Asciminib, Vandetanib, Neratinib, Afatinib, Erdafitinib, Trametinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81643/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81543, 'Deferasirox', 'Phenobarbital', 'Moderate', 'Coadministration with inducers of uridine diphosphate glucuronosyltransferase (UGT) may decrease the plasma concentrations of deferasirox, which is primarily metabolized by UGT1A1- and UGT1A3-mediated glucuronidation.', NULL, 'The concomitant use of deferasirox with potent UGT inducers such as rifampin, phenytoin, phenobarbital, carbamazepine, and ritonavir should generally be avoided.', NULL, 'The concomitant use of deferasirox with potent UGT inducers such as rifampin, phenytoin, phenobarbital, carbamazepine, and ritonavir should generally be avoided. Otherwise, consideration may be given to increasing the initial dosage of deferasirox by 50% during coadministration with potent UGT inducers, with subsequent adjustments made according to serum ferritin levels and clinical response.', 'Metabolism', 'Flurazepam, Methohexital, Methylphenobarbital, Pentobarbital, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Butabarbital, Secobarbital, Chloral hydrate, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Dexrazoxane, Oxygen, Lanthanum carbonate, Deferoxamine, Arginine, Methionine, Deferiprone, Potassium Iodide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81644/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Exjade (deferasirox)." Novartis Phar