INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81609, 'Deferasirox', 'Streptokinase', 'Major', 'Coadministration with drugs that have known ulcerogenic or hemorrhagic potential may increase the risk of gastrointestinal bleeding associated with the use of deferasirox. Fatal gastrointestinal hemorrhages have been reported during deferasirox therapy, especially in elderly patients with advanced hematologic malignancies and/or low platelet counts.', NULL, 'Caution is advised if deferasirox is prescribed with drugs that have known ulcerogenic or hemorrhagic potential, including but not limited to NSAIDs, salicylates, corticosteroids, oral bisphosphonates, platelet inhibitors, anticoagulants, thrombolytic agents, or agents that commonly cause thrombocytopenia.', NULL, 'Caution is advised if deferasirox is prescribed with drugs that have known ulcerogenic or hemorrhagic potential, including but not limited to NSAIDs, salicylates, corticosteroids, oral bisphosphonates, platelet inhibitors, anticoagulants, thrombolytic agents, or agents that commonly cause thrombocytopenia. Patients should be advised to contact their physician if they develop potential signs and symptoms of gastrointestinal injury such as abdominal pain, bloating, dizziness, lightheadedness, hematemesis, anorexia, and/or black, tarry stools.', 'Synergism', 'Protein C, Acenocoumarol, Hemin, Voxelotor, Conestat alfa, Hyaluronidase, Human C1-esterase inhibitor, Berotralstat, Lanadelumab, Icatibant, Crizanlizumab', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/serv