INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81964, 'Defibrotide', 'Tirofiban', 'Major', 'Coadministration of defibrotide with antiplatelet agents or other agents that affect coagulation may increase the risk of bleeding. In vitro, defibrotide increased the activity of fibrinolytic enzymes. In a mouse model of thromboembolism, recombinant tissue plasminogen activator (t-PA) potentiated the antithrombotic effect of defibrotide when given intravenously.', NULL, 'Concomitant use of medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with defibrotide.', NULL, 'Concomitant use of medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with defibrotide. Close clinical and laboratory observation for bleeding complications is recommended during therapy. If bleeding develops, defibrotide should be discontinued and treatment/supportive care provided until the bleeding stops.', 'Synergism', 'Defibrotide, Protein C, Acenocoumarol, Selexipag', 'Tirofiban, Antithrombin III human, Protein C, Acenocoumarol, Selexipag', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82065/', '[1] "Product Information. Defitelio (defibrotide)." Jazz Pharmaceuticals, Palo Alto, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81965, 'Defibrotide', 'Tolmetin', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Tolazoline, Rabeprazole, Dimethyl sulfoxide', 'Protein C, Acenocoumarol, Selexipag', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82066/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81966, 'Defibrotide', 'Tositumomab', 'Major', 'Coadministration of tositumomab and iodine I 131 tositumomab with drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Tositumomab and iodine I 131 tositumomab alone can cause severe or life-threatening thrombocytopenia.', NULL, 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.', NULL, 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously. Close clinical and laboratory observation for bleeding complications is recommended during and after the therapeutic regimen.', 'Synergism', 'Iodide I-131', 'Dicoumarol, Desirudin, Protein C, Acenocoumarol, Selexipag, Anistreplase', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82067/', '[1] "Product Information. BexxarTherapy (tositumomab)." GlaxoSmithKline Inc, Oakville, IA.[2] "Product Information. Bexxar I 131 Therapeutic (iodine I 131 tositumomab)." GlaxoSmithKline, Research Triangle Park, NC.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81967, 'Defibrotide', 'Treprostinil', 'Moderate', 'Prostacyclin/prostacyclin analogs (e.g., epoprostenol, iloprost, treprostinil) inhibit platelet aggregation and may theoretically potentiate the risk of bleeding in patients treated with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', NULL, 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Defibrotide, Protein C, Acenocoumarol, Selexipag', 'Protein C, Acenocoumarol, Selexipag, Epoprostenol, Treprostinil', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82068/', '[1] "Product Information. Ventavis (iloprost)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[2] "Product Information. Flolan (epoprostenol)." Glaxo Wellcome, Research Triangle Park, NC.[3] "Product Information. Remodulin (treprostinil)." United Therapeutics Corp, Silver Spring, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (81968, 'Defibrotide', 'Turmeric', 'Minor', 'Theoretically, turmeric may potentiate the effects of anticoagulants, platelet inhibitors, and thrombolytic agents, possibly increasing the risk of bleeding. In vitro data suggest that turmeric may inhibit platelet-activating factor and platelet aggregation. Patients should consult a healthcare provider before taking any herbal or alternative medicine. In patients who have used turmeric extensively prior to receiving anticoagulation, antiplatelet, or thrombolytic therapy, the potential for an interaction should be considered. Clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', NULL, '-', NULL, '-', 'Synergism', 'Protein C, Acenocoumarol, Selexipag', NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82069/', '[1] Abebe W "Herbal medication: potential for adverse interactions with analgesic drugs." J Clin Pharm Ther 27 (2002): 391-401[2] Yang X, Thomas DP, Zhang X, et al "Curcumin inhibits platelet-derived growth factor-stimulated vascular smooth muscle cell function and injury-induced neointima formation." Arterioscler Thromb Vasc Biol 26 (2006): 85-90[3] Heck AM, DeWitt BA, Lukes AL "Potential interactions between alternative therapies and warfarin." Am J Health Syst Pharm 57 (2000): 1221-7; quiz 1228-30',