INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82202, 'Deflazacort', 'Roflumilast', 'Moderate', 'The concomitant use of roflumilast with immunosuppressive agents has not been adequately studied. Specifically, roflumilast and its active metabolite, roflumilast N-oxide, suppress the release of inflammatory mediators including leukotriene B4, reactive oxygen species, tumor necrosis factor alpha, interferon gamma, and granzyme B. Roflumilast also reduces sputum neutrophils and attenuates influx of neutrophils and eosinophils into the airways.', NULL, 'According to the product labeling for roflumilast in some countries , concomitant use with immunosuppressive agents such as methotrexate, azathioprine, infliximab, etanercept, and systemic corticosteroids (except when used short-term, e.', NULL, 'According to the product labeling for roflumilast in some countries , concomitant use with immunosuppressive agents such as methotrexate, azathioprine, infliximab, etanercept, and systemic corticosteroids (except when used short-term, e.g., in the treatment of COPD exacerbations) should be avoided if possible. Due to a lack of clinical experience, treatment with roflumilast should not be initiated, or existing treatment should be stopped, in patients receiving immunosuppressive agents and in patients with severe acute infectious diseases, cancers (except basal cell carcinoma), or severe immunological diseases (e.g., HIV infection, multiple sclerosis, lupus erythematosus, progressive multifocal leukoencephalopathy).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82303/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Daxas (roflumilast)." Nycomed Inc, Princeton, NJ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Daxas (roflumilast)." Nycomed Inc, Princeton, NJ.[5] "Product Information. Daliresp (roflumilast)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82203, 'Deflazacort', 'Romosozumab', 'Moderate', 'Coadministration of romosozumab with bisphosphonates, denosumab, angiogenesis inhibitors, or corticosteroids may increase the risk of developing osteonecrosis of the jaw (ONJ). The condition can occur spontaneously and is generally associated with tooth extraction and/or local infection with delayed healing. Other risk factors for ONJ include cancer, chemotherapy, radiotherapy to the head and neck, poor oral hygiene, preexisting dental disease or infection, anemia, and coagulopathy.', NULL, 'Caution is advised when romosozumab is used with other agents that are also associated with osteonecrosis of the jaw.', NULL, 'Caution is advised when romosozumab is used with other agents that are also associated with osteonecrosis of the jaw. A routine oral examination should be performed by the prescriber prior to initiation of romosozumab treatment. For patients requiring invasive dental procedures, clinical judgment and risk-benefit assessment should guide the management plan of each patient based on their clinical circumstances. Patients should be advised to seek medical attention if they experience signs and symptoms of ONJ, such as: pain in the mouth, teeth, or jaw; swelling or sores inside the mouth; numbness or a feeling of heaviness in the jaw; loosening of a tooth; or exposure of bone in the jaw.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82304/', '[1] "Product Information. Evenity (romosozumab)." Amgen USA, Thousand Oaks, CA.', 1767363022);
