INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82443, 'Bedaquiline', 'Prasterone enantate', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82544/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[3] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82444, 'Diazepam', 'Prasterone enantate', 'Minor', 'Dehydroepiandosterone may increase serum concentrations of some benzodiazepines. The mechanism is inhibition of the CYP 3A4 enzyme responsible for metabolism of these drugs. Patients should be observed for adverse effects and dosage adjustments should be made if indicated.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82545/', '[1] Frye RF, Kroboth PD, Folan MM, et al. "Effect of DHEA on CYP3A4-mediated metabolism of triazolam." Clin Pharmacol Ther 67 (2000): 109[2] Kupferschmidt HHT, Ha HR, Ziegler WH, Meier PJ, Krahenbuhl S "Interaction between grapefruit juice and midazolam in humans." Clin Pharmacol Ther 58 (1995): 20-8[3] Hukkinen SK, Varhe A, Olkkola KT, Neuvonen PJ "Plasma concentrations of triazolam are increased by concomitant ingestion of grapefruit juice." Clin Pharmacol Ther 58 (1995): 127-31[4] "Product Information. Valium (diazepam)." Roche Laboratories, Nutley, NJ.[5] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[6] "Product Information. Halcion (triazolam)." Pharmacia and Upjohn, Kalamazoo, MI.[7] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[8] "Product Information. Xanax (alprazolam)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82445, 'Efavirenz', 'Prasterone enantate', 'Moderate', 'Coadministration of efavirenz with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Efavirenz has been associated with hepatotoxicity during postmarketing use.', NULL, 'The risk of hepatic injury should be considered when efavirenz is used in combination with other agents that are potentially hepatotoxic (e.', NULL, 'The risk of hepatic injury should be considered when efavirenz is used in combination with other agents that are potentially hepatotoxic (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; other HIV reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of liver function tests should occur before and during treatment, especially in patients with underlying hepatic disease (including hepatitis B or C coinfection) or marked transaminase elevations.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82546/', '[1] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[2] Elsharkawy AM, Schwab U, McCarron B, et al. "Efavirenz induced acute liver failure requiring liver transplantation in a slow drug metaboliser." J Clin Virol 58 (2013): 331-3[3] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[4] "Product Information. Retrovir (zidovudine)." Glaxo Wellcome, Research Triangle Park, NC.[5] Lotterer E, Ruhnke M, Trautman M, et al "Decreased and variable systemic availability of zidovudine in patients with AIDS if administered with a meal." Eur J Clin Pharmacol 40 (1991): 305-8[6] Unadkat JD, Collier AC, Crosby SS, et al "Pharmacokinetics of oral zidovudine (azidothymidine) in patients with AIDS when administered with and without a high-fat meal." AIDS 4 (1990): 229-32[7] Sahai J, Gallicano K, Garber G, et al. "The effect of a protein meal on zidovudine pharmacokinetics in HIV-infected patients." Br J Clin Pharmacol 33 (1992): 657-60', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82446, 'Elagolix', 'Prasterone enantate', 'Moderate', 'Since endometriosis is fueled by estrogen, coadministration with estrogen-containing medications including combination oral contraceptive pills is expected to reduce the efficacy of elagolix. The effect of progestin-only contraceptives is unknown. Elagolix is a gonadotropin-releasing hormone (GnRH) receptor antagonist that inhibits endogenous GnRH signaling by binding competitively to GnRH receptors in the pituitary gland. Administration of elagolix results in dose-dependent suppression of luteinizing hormone (LH) and follicle-stimulating hormone (FSH), leading to decreased levels of the ovarian sex hormones, estradiol and progesterone.', NULL, 'Women requiring contraception should be advised to use non-hormonal forms of contraception during treatment with elagolix and for one week after its discontinuation.', NULL, 'Women requiring contraception should be advised to use non-hormonal forms of contraception during treatment with elagolix and for one week after its discontinuation.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82547/', '[1] "Product Information. Orilissa (elagolix)." AbbVie US LLC, North Chicago, IL.[2] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids in grapefruit juice inhibit the in vitro hepatic metabolism of 17B-estradiol." Eur J Drug Metab Pharmacokinet 20 (1995): 219-24[3] Weber A, Jager R, Borner A, et al. "Can grapefruit juice influence ethinyl estradiol bioavailability?" Contraception 53 (1996): 41-7', 1767363022);
