INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1372975, 'Brivaracetam', 'Sodium oxybate', 'Major', 'The central nervous system and respiratory depressant effects of sodium oxybate, which is the sodium salt of gamma hydroxybutyrate (GHB), may be potentiated by concomitant use of other agents with CNS depressant effects. An increased risk of serious adverse reactions such as respiratory depression, hypotension, profound sedation, syncope, coma, and even death should be considered.', 'DDInter', 1767317475, 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible. Otherwise, close monitoring and/or dosage reductions should be considered. If short-term use of a CNS depressant is required (e.g., post- or perioperative opioid), a temporary interruption of sodium oxybate therapy may be appropriate.', 'Synergism', 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1372976, 'Brivaracetam', 'St. John''s Wort', 'Moderate', 'Coadministration with potent inducers of CYP450 isoenzymes may decrease the plasma concentrations of brivaracetam, which is partially metabolized by CYP450 2C19.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of brivaracetam should be considered during coadministration with potent CYP450 enzyme inducers. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 inducer is added to or withdrawn from therapy. For patients receiving concomitant carbamazepine or phenytoin therapy, blood levels should be monitored following initiation or discontinuation of brivaracetam. Dosage reductions for carbamazepine and phenytoin may be required if tolerability issues arise.', 'Metabolism', 'The potential for diminished pharmacologic effects of brivaracetam should be considered during coadministration with potent CYP450 enzyme inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
