INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82634, 'Delavirdine', 'Astemizole', 'Major', 'Coadministration with delavirdine may significantly increase the plasma concentrations of astemizole and terfenadine. The mechanism is delavirdine inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of these antihistamines. High plasma levels of astemizole and terfenadine (e.g., due to overdose or interaction with other potent 3A4 inhibitors) have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes; cardiac arrest; and sudden death.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, the concomitant use with delavirdine is considered contraindicated.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, the concomitant use with delavirdine is considered contraindicated. Loratadine, cetirizine, or fexofenadine may be safer alternatives.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82735/', '[1] "Product Information. Rescriptor (delavirdine)." Pharmacia and Upjohn, Kalamazoo, MI.[2] Honig PK, Wortham DC, Zamani K, et al "Terfenadine-ketoconazole interaction: pharmacokinetic and electrocardiographic consequences." JAMA 269 (1993): 1513-8[3] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[4] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[5] Zimmermann M, Duruz H, Guinand O, et al "Torsades de Pointes after treatment with terfenadine and ketoconazole." Eur Heart J 13 (1992): 1002-3[6] Honig PK, Wortham DC, Lazarev A, Cantilena LR "Grapefruit juice alters the systemic bioavailability and cardiac repolarization of terfenadine in poor metabolizers of terfenadine." J Clin Pharmacol 36 (1996): 345-51[7] Benton RE, Honig PK, Zamani K, Cantilena LR, Woosley RL "Grapefruit juice alters terfenadine pharmacokinetics resulting in prolongation of repolarization on the electrocardiogram." Clin Pharmacol Ther 59 (1996): 383-8[8] Clifford CP, Adams DA, Murray S, Taylor GW, Wilkins MR, Boobis AR, Davies DS "Pharmacokinetic and cardiac effects of terfenadine after inhibition of its metabolism by grapefruit juice." Br J Clin Pharmacol 42 (1996): p662[9] Zechnich AD, Haxby DG "Drug interactions associated with terfenadine and related nonsedating antihistamines." West J Med 164 (1996): 68-9[10] Rau SE, Bend JR, Arnold JMO, Tran LT, Spence JD, Bailey DG "Grapefruit juice terfenadine single-dose interaction: Magnitude, mechanism, and relevance." Clin Pharmacol Ther 61 (1997): 401-9[11] Honig PK, Woosley RL, Zamani K, Conner DP, Cantilena LR Jr "Changes in the pharmacokinetics and electrocardiographic pharmacodynamics of terfenadine with concomitant administration of erythromycin." Clin Pharmacol Ther 52 (1992): 231-8[12] Monahan BP, Ferguson CL, Killeavy ES, et al "Torsades de pointes occurring in association with terfenadine use." JAMA 264 (1990): 2788-90[13] Cortese LM, Bjornson DC "Potential interaction between terfenadine and macrolide antibiotics." Clin Pharm 11 (1992): 675[14] Pohjola-Sintonen S, Viitasalo M, Toivonene L, Neuvonen P "Torsades de pointes after terfenadine-itraconazole interaction." BMJ 306 (1993): 186[15] Mathews DR, McNutt B, Okerholm R, et al "Torsades de pointes occurring in association with terfenadine use." JAMA 266 (1991): 2375-6[16] Hsieh MH, Chen SA, Chiang CE, et al. "Drug-induced torsades de pointes in one patient with congenital long QT syndrome." Int J Cardiol 54 (1996): 85-8[17] Woosley RL "Cardiac actions of antihistamines." Annu Rev Pharmacol Toxicol 36 (1996): 233-52[18] Paris DG, Parente TF, Bruschetta HR, Guzman E, Niarchos AP "Torsades-de-pointes induced by erythromycin and terfenadine." Am J Emerg Med 12 (1994): 636-8', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82635, 'Delavirdine', 'Bedaquiline', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to its less active N-monodesmethyl metabolite, M2.', NULL, 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk.', NULL, 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk. Appropriate clinical monitoring for bedaquiline-related adverse reactions is recommended if coadministration is required. ECG, serum electrolytes (potassium, magnesium, calcium), and liver function tests (ALT, AST, alkaline phosphatase, bilirubin) should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration. Bedaquiline should be interrupted in patients who develop clinically significant ventricular arrhythmia or a QTcF interval greater than 500 msec confirmed by repeat ECG. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82736/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.', 1767363022);
