INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82713, 'Idarubicin', 'Delavirdine', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', NULL, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82814/', '[1] "Product Information. Daunoxome (daunorubicin liposomal)." Nexstar Pharmaceuticals Inc, Boulder, CO.[2] "Product Information. Idamycin (idarubicin)." Pharmacia and Upjohn, Kalamazoo, MI.[3] "Multum Information Services, Inc. Expert Review Panel"[4] "Product Information. Cerubidine (daunorubicin)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82714, 'Idelalisib', 'Delavirdine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of idelalisib, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'Caution is advised if idelalisib is prescribed in combination with CYP450 3A4 and/or P-gp inhibitors.', NULL, 'Caution is advised if idelalisib is prescribed in combination with CYP450 3A4 and/or P-gp inhibitors. Pharmacologic response to idelalisib should be monitored more closely whenever a CYP450 3A4/P-gp inhibitor is added to or withdrawn from therapy, and the idelalisib dosing adjusted or interrupted as necessary in accordance with the product labeling. Patients should be closely monitored for idelalisib toxicity such as hepatotoxicity, diarrhea, colitis, intestinal perforation, pneumonitis, neutropenia, and thrombocytopenia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82815/', '[1] "Product Information. Zydelig (idelalisib)." Gilead Sciences, Foster City, CA.[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82715, 'Iloperidone', 'Delavirdine', 'Major', 'Coadministration with inhibitors of CYP450 3A4 and/or 2D6 may increase the plasma concentrations of iloperidone, which is a substrate of the isoenzymes.', NULL, 'The dosage of iloperidone should be reduced by one-half when administered concomitantly with potent CYP450 3A4 inhibitors (e.', NULL, 'The dosage of iloperidone should be reduced by one-half when administered concomitantly with potent CYP450 3A4 inhibitors (e.g., itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, clarithromycin, telithromycin) and/or potent CYP450 2D6 inhibitors (e.g., dacomitinib, fluoxetine, paroxetine, quinidine). Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope. Following discontinuation of the potent CYP450 3A4 or 2D6 inhibitor, the iloperidone dosage should be returned to the previous level.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82816/', '[1] "Product Information. Fanapt (iloperidone)." Vanda Pharmaceuticals Inc, Rockville, MD.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82716, 'Imatinib', 'Delavirdine', 'Moderate', 'Imatinib is a substrate of the CYP450 3A4 isoenzyme as well as a potent competitive inhibitor of the CYP450 2C9, 2D6 and 3A4 isoenzymes. Some drugs that are known inhibitors of CYP450 3A4 are also metabolized by one or more of the isoenzymes inhibited by imatinib. Theoretically, coadministration of imatinib with those drugs may result in mutually elevated plasma drug concentrations due to competitive and noncompetitive inhibition of CYP450 activities.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of imatinib, including serious adverse effects such as edema, hematologic toxicity and immunosuppression, should be considered during concomitant therapy, particularly with potent CYP450 3A4 inhibitors such as ketoconazole, itraconazole, ritonavir, nefazodone, and erythromycin.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of imatinib, including serious adverse effects such as edema, hematologic toxicity and immunosuppression, should be considered during concomitant therapy, particularly with potent CYP450 3A4 inhibitors such as ketoconazole, itraconazole, ritonavir, nefazodone, and erythromycin. In addition, clinical and laboratory monitoring for potentially increased pharmacologic effects of coadministered medications is recommended, especially those with a narrow therapeutic range.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82817/', '[1] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product 