INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82728, 'Delavirdine', 'Ivosidenib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivosidenib, which is primarily metabolized by the isoenzyme. Coadministration with ivosidenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4, including many of the known potent inhibitors of the isoenzyme such as clarithromycin, conivaptan, delavirdine, idelalisib, itraconazole, ketoconazole, nefazodone, posaconazole, telithromycin, and all protease inhibitors. Ivosidenib is an inducer of CYP450 3A4 and induces its own metabolism following multiple dosing.', NULL, 'Concomitant use of ivosidenib with potent CYP450 3A4 inhibitors, particularly those that are also substrates of CYP450 3A4, should generally be avoided.', NULL, 'Concomitant use of ivosidenib with potent CYP450 3A4 inhibitors, particularly those that are also substrates of CYP450 3A4, should generally be avoided. If coadministration is required, a reduction of the ivosidenib dosage to 250 mg once daily is recommended. Following discontinuation of the potent inhibitor for at least 5 half-lives, ivosidenib therapy should be returned to the recommended dosage of 500 mg once daily. Monitoring of electrocardiograms and electrolytes should be conducted regularly.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82829/', '[1] "Product Information. Tibsovo (ivosidenib)." Agios Pharmaceuticals, Cambridge, MA.[2] "Product Information. Tibsovo (ivosidenib)." Agios Pharmaceuticals, Cambridge, MA.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82729, 'Delavirdine', 'Ixabepilone', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ixabepilone, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of ixabepilone with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of ixabepilone with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is necessary, a reduction of the ixabepilone dosage to 20 mg/m2 should be considered. Based on pharmacokinetic studies, this dosage is predicted to adjust the ixabepilone systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the ixabepilone dosage is adjusted upward to the indicated dosage.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82830/', '[1] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.', 1767363022);
