INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82732, 'Delavirdine', 'Lapatinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lapatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of lapatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of lapatinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of lapatinib during and for 2 weeks after treatment with itraconazole. If coadministration is required, the manufacturer recommends reducing the lapatinib dosage to 500 mg once a day. However, clinical data are lacking. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the lapatinib dosage is adjusted upward to the indicated dosage (i.e., 1250 mg once a day).', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82833/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363022);
