INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82805, 'Delavirdine', 'Pexidartinib', 'Major', 'Coadministration of pexidartinib with strong CYP450 3A4 inhibitors and/or uridine diphosphate glucuronosyltransferase (UGT) inhibitors may significantly increase the plasma concentrations and the incidence and severity of adverse effects of pexidartinib, including potentially fatal hepatotoxicity. The proposed mechanism is inhibition of CYP450 3A4 and/or UGT, the primary isoenzymes responsible for the metabolic clearance of pexidartinib.', NULL, 'The use of pexidartinib with strong CYP450 3A4 inhibitors and/or UGT inhibitors should generally be avoided.', NULL, 'The use of pexidartinib with strong CYP450 3A4 inhibitors and/or UGT inhibitors should generally be avoided. If concomitant use is required, the dose of pexidartinib should be reduced according to the manufacturer''s recommendations. If concomitant use of a strong CYP450 3A4 inhibitor or UGT inhibitor is discontinued, the dose of pexidartinib may be increased, after 3 plasma half-lives of the strong CYP450 3A4 inhibitor or UGT inhibitor, to the dose that was used prior to starting the strong CYP450 3A4 inhibitor or UGT inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82906/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (vorico