INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82874, 'Delavirdine', 'Tadalafil', 'Moderate', 'Coadministration with drugs that are potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of tadalafil with potent 3A4 inhibitors is not recommended in patients being treated for pulmonary arterial hypertension.', NULL, 'Concomitant use of tadalafil with potent 3A4 inhibitors is not recommended in patients being treated for pulmonary arterial hypertension. In addition, some authorities recommend avoiding concomitant use of tadalafil during and for 2 weeks after treatment with itraconazole in patients with pulmonary arterial hypertension. For erectile dysfunction, tadalafil labeling recommends that the dosage not exceed 10 mg once every 72 hours when taken on an as needed basis in patients treated concomitantly with a potent CYP450 3A4 inhibitor. When taken on a daily basis, the dose should not exceed 2.5 mg per day. Patients should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82975/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[7] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[8] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82875, 'Delavirdine', 'Talazoparib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of talazoparib, which has been shown in vitro to be a substrate of both efflux membrane transporters.', NULL, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.', NULL, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil. However, patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82976/', '[1] "Product Information. Talzenna (talazoparib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82876, 'Delavirdine', 'Tamsulosin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of tamsulosin, which is primarily metabolized by the hepatic microsomal isoenzymes CYP450 3A4 and 2D6. Severe hypotension and priapism may occur.', NULL, 'Since CYP450 2D6 poor metabolizers cannot be readily identified, concomitant use of tamsulosin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Since CYP450 2D6 poor metabolizers cannot be readily identified, concomitant use of tamsulosin with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of tamsulosin during and for 2 weeks after treatment with itraconazole. If tamsulosin administration is discontinued for several days or more at either the 0.4 or 0.8 mg dose, therapy should be reinitiated with the 0.4 mg once-daily dose and titrated gradually as needed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82977/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] "Product Information. Flomax (tamsulosin)." Boehringer-Ingelheim, Ridgefield, CT.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Franco-Salinas G, de la Rosette JJ, Michel MC "Pharmacokinetics and pharmacodynamics of tamsulosin in its modified-release and oral controlled absorption system formulations." Clin Pharmacokinet 49 (2010): 177-88[5] Kamimura H, Oishi S, Matsushima H, et al. "Identification of cytochrome P450 isozymes involved in metabolism of the alpha1-adrenoceptor blocker tamsulosin in human liver microsomes." Xenobiotica 28 (1998): 909-22[6] "Product Information. Duagen (dutasteride)." GlaxoSmithKline Healthcare, Pittsburgh, PA.[7] "Product Information. Flomax (tamsulosin)." Boehringer-Ingelheim, Ridgefield, CT.[8] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[9] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82877, 'Delavirdine', 'Tasimelteon', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of tasimelteon, which is primarily metabolized by these isoenzymes.', NULL, 'Caution is advised if tasimelteon is prescribed in combination with inhibitors of CYP450 1A2 and/or 3A4.', NULL, 'Caution is advised if tasimelteon is prescribed in combination with inhibitors of CYP450 1A2 and/or 3A4. Patients should be monitored for excessive sedation and other side effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82978/', '[1] "Product Information. Hetlioz (tasimelteon)." Vanda Pharmaceuticals Inc, Rockville, MD.[2] "Product Information. Hetlioz (tasimelteon)." Vanda Pharmaceuticals Inc, Rockville, MD.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82878, 'Delavirdine', 'Tazemetostat', 'Major', 'Coadministration with moderate or potent CYP450 3A4 inhibitors may significantly increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in an increased risk of the frequency or severity of adverse reactions due to tazemetostat such as secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', NULL, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided. Alternative therapeutic agents with less inhibitory potential should be considered whenever possible. If coadministration with a moderate CYP450 3A4 inhibitor is required and no other alternatives are possible, the dose of tazemetostat should be reduced as per manufacturer recommendations. After the inhibitor has been discontinued for 3 elimination half-lives, the tazemetostat dose that was taken prior to initiating the inhibitor may be resumed. Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82979/', '[1] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc, Cambridge, MA.[2] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc, Cambridge, MA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82879, 'Delavirdine', 'Telithromycin', 'Moderate', 'Coadministration with telithromycin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by telithromycin.', NULL, 'Caution is advised if telithromycin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if telithromycin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever telithromycin is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82980/', '[1] "Product Information. Ketek (telithromycin)." Aventis Pharmaceuticals, Bridgewater, NJ.[2] European Agency for the Evaluation of Medicinal Products. Committee for Proprietary Medicinal Products "European Public Assessment Report Ketek (telithromycin) (Rev. 2) Available from: URL: http:www.emea.eu.int/humandocs/Humans/EPAR/Ketek/Ketek.htm." ([2001]):[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82880, 'Delavirdine', 'Telotristat ethyl', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes. The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82981/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.[3] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82881, 'Delavirdine', 'Temsirolimus', 'Major', 'Coadministration of temsirolimus with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', NULL, 'Concomitant use of temsirolimus with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of temsirolimus with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of intravenous temsirolimus during and for 2 weeks after treatment with itraconazole (US). If coadministration is required, the manufacturer recommends reducing the temsirolimus dosage to 12.5 mg once a week. Based on pharmacokinetic studies, this dosage is predicted to adjust the sirolimus systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the temsirolimus dosage is adjusted upward to the normally recommended dosage (i.e., 25 mg once a week) or the dosage used prior to initiation of the CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82982/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the 