INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82888, 'Delavirdine', 'Ticagrelor', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ticagrelor, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of ticagrelor with potent CYP450 3A4 inhibitors is considered contraindicated by some foreign authorities.', NULL, 'Concomitant use of ticagrelor with potent CYP450 3A4 inhibitors is considered contraindicated by some foreign authorities. The U.S. labeling for ticagrelor recommends avoiding use with potent CYP450 3A4 inhibitors. Labeling for itraconazole, a potent CYP450 3A4 inhibitor, specifically contraindicates use with ticagrelor during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82989/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Brilinta (ticagrelor)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[4] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82889, 'Delavirdine', 'Timolol (ophthalmic)', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 may increase the systemic effects of topically administered timolol, which is metabolized by the isoenzyme. Following ocular administration, timolol is systemically absorbed and can reach plasma levels associated with adverse beta-adrenergic blocking effects such as bronchospasm, depression, bradycardia, and hypotension. The risk may be increased if clearance of the drug is significantly diminished by concomitant CYP450 2D6 inhibitors.', NULL, 'Patients should be monitored for systemic beta-adrenergic blocking effects of topical timolol during coadministration with CYP450 2D6 inhibitors such as cimetidine, quinidine, and certain selective serotonin reuptake inhibitors.', NULL, 'Patients should be monitored for systemic beta-adrenergic blocking effects of topical timolol during coadministration with CYP450 2D6 inhibitors such as cimetidine, quinidine, and certain selective serotonin reuptake inhibitors. Particular caution is warranted in elderly patients, since they are generally more susceptible to adverse effects of topically administered beta blockers.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82990/', '[1] Fraunfelder FT, Fraunfelder FW; Randall JA "Drug-Induced Ocular Side Effects 5th" Boston, MA: Butterworth-Heinemann (2001):[2] Edeki TI, He HB, Wood AJJ "Pharmacogenetic explanation for excessive beta-blockade following timolol eye drops: potential for oral-ophthalmic drug interaction." JAMA 274 (1995): 1611-3[3] Lewis RV, Lennard MS, Jackson PR, Tucker GT, Ramsay LE, Woods HF "Timolol and atenolol: relationships between oxidation phenotype, pharmacokinetics and pharmacodynamics." Br J Clin Pharmacol 19 (1985): 329-33[4] Dinai Y, Sharir M, Floman NN, Halkin H "Bradycardia induced by interaction between quinidine and ophthalmic timolol." Ann Intern Med 103 (1985): 890-1[5] Higginbotham E "Topical beta-adrenergic antagonists and quinidine: a risky interaction." Arch Ophthalmol 114 (1996): 745-6[6] Ishii Y, Nakamura K, Tsutsumi K, Kotegawa T, Nakano S, Nakatsuka K "Drug interaction between cimetidine and timolol ophthalmic solution: Effect on heart rate and intraocular pressure in healthy Japanese volunteers." J Clin Pharmacol 40 (2000): 193-9[7] Alvan G, Calissendorff B, Seideman P, Widmark K, Widmark G "Absorption of ocular timolol." Clin Pharmacokinet 5 (1980): 95-100', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82890, 'Delavirdine', 'Tofacitinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of tofacitinib, which is metabolized by the isoenzyme. Side effects including lymphopenia, neutropenia, anemia, serious infections, and hyperlipidemia may be increased.', NULL, 'The dosage of tofacitinib should be reduced by 50% when used with potent CYP450 3A4 inhibitors.', NULL, 'The dosage of tofacitinib should be reduced by 50% when used with potent CYP450 3A4 inhibitors. For example, the dose for patients receiving 10 mg twice daily should be reduced to 5 mg twice daily and the dose for patients receiving 5 mg twice daily should be reduced to 5 mg once daily. For patients receiving 11 mg once daily of the extended-release formulation, the dose should be reduced to 5 mg once daily of the immediate-release formulation. The dose for patients receiving 3.2 mg twice daily should be reduced to 3.2 mg once daily and the dose for patients receiving 4 mg twice daily should be reduced to 4 mg once daily.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82991/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82891, 'Delavirdine', 'Tolterodine', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the plasma concentrations of tolterodine, which is partially metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of tolterodine should be considered. Although tolterodine is primarily metabolized by CYP450 2D6, there is some evidence that CYP450 3A4 may play a minor role, thus any alteration in its activity levels could conceivably affect the metabolism of tolterodine.', NULL, 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors.', NULL, 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors. Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience an irregular heartbeat, severe blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, or GI upset.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82992/', '[1] "Product Information. Detrol (tolterodine)." Pharmacia and Upjohn, Kalamazoo, MI.[2] Brynne N, Forslund C, Hallen B, Gustafsson LL, Bertilsson L "Ketoconazole inhibits the metabolism of tolterodine in subjects with deficient CYP2D6 activity." Br J Clin Pharmacol 48 (1999): 564-72', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82892, 'Delavirdine', 'Tolvaptan', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of vasopressin receptor antagonists conivaptan and tolvaptan, both of which are almost exclusively metabolized by the isoenzyme.', NULL, 'Concomitant use of conivaptan or tolvaptan with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of conivaptan or tolvaptan with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities recommend avoiding concomitant use of conivaptan or tolvaptan during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82993/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[4] "Product Information. Vaprisol (conivaptan)." Cumberland Pharmaceuticals Inc, Nashville, TN.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[7] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82893, 'Delavirdine', 'Toremifene', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of toremifene, which is primarily metabolized by the isoenzyme. Because toremifene is associated with dose- and concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'The use of toremifene in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', NULL, 'The use of toremifene in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. If treatment with one of these agents is required, interruption of toremifene therapy should be considered. Otherwise, patients who require concomitant treatment should be closely monitored. In patients at increased risk, electrocardiograms (ECGs) should be obtained at baseline and as clinically indicated. Complete blood counts, electrolyte levels (calcium, magnesium, potassium), and liver function tests should also be obtained periodically. Patients should be advised to contact their physician if they experience vaginal bleeding or potential signs of blood clots such as chest pain, shortness of breath, sudden loss of vision, and pain, redness or swelling in an extremity. Patients should seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82994/', '[1] "Product Information. Fareston (toremifene)." Schering Laboratories, Kenilworth, NJ.[2] "Product Information. Fareston (toremifene)." Schering Laboratories, Kenilworth, NJ.[3] Therapeutic Research Faculty "Natural Medicines Comprehensive Database. Available from: URL: http://www.naturaldatabase.com." ([1995-2008...]):[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82894, 'Delavirdine', 'Torasemide', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of torsemide. The interaction has not been evaluated in pharmacokinetic studies; however, torsemide has been shown to undergo metabolism by CYP450 2C9.', NULL, 'Caution is advised when torsemide is prescribed with CYP450 2C9 inhibitors.', NULL, 'Caution is advised when torsemide is prescribed with CYP450 2C9 inhibitors. Patients should be monitored for potentially increased adverse effects such as dehydration, hypotension, electrolyte imbalances, hyperglycemia, tinnitus, and hearing loss.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82995/', '[1] Vormfelde SV, Engelhardt S, Zirk A, et al. "CYP2C9 polymorphisms and the interindividual variability in pharmacokinetics and pharmacodynamics of the loop diuretic drug torsemide." Clin Pharmacol Ther 76 (2004): 557-66[2] Miners JO, Rees DLP, Valente L, Veronese ME, Birkett DJ "Human hepatic cytochrome p450 2c9 catalyzes the rate-limiting pathway of torsemide metabolism." J Pharmacol Exp Ther 272 (1995): 1076-81[3] "Product Information. Demedex (torsemide)." Boehringer Mannheim, Gaithersburg, MD.[4] Miners JO, Birkett DJ "Cytochrome P4502C9: an enzyme of major importance in human drug metabolism." Br J Clin Pharmacol 45 (1998): 525-38[5] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82895, 'Delavirdine', 'Trabectedin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of trabectedin, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of trabectedin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of trabectedin with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of trabectedin during and for 2 weeks after treatment with itraconazole. If a potent CYP450 3A4 inhibitor is required for short-term use (i.e., less than 14 days), it is advisable to administer the CYP450 3A4 inhibitor one week after the trabectedin infusion and discontinue it the day prior to the next infusion. Close monitoring for toxicities such as myelosuppression, rhabdomyolysis, hepatotoxicity, and cardiomyopathy is recommended during long-term coadministration, and the trabectedin dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82996/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[4] Machiels JP, Staddon A, Herremans C, et al. "Impact of cytochrome P450 3A4 inducer and inhibitor on the pharmacokinetics of trabectedin in patients with advanced malignancies: open-label, multicenter studies." Cancer Chemother Pharmacol 74 (2014): 729-37[5] "Product Information. Sporanox (itra