INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83288, 'Dofetilide', 'Desipramine', 'Major', 'Like other class III antiarrhythmic agents, dofetilide can cause dose-related QT interval prolongation. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of dofetilide with other drugs that can prolong the QT interval has not been studied and is not recommended.', NULL, 'Coadministration of dofetilide with other drugs that can prolong the QT interval has not been studied and is not recommended.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83389/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[5] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[6] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[7] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[8] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83289, 'Dolasetron', 'Desipramine', 'Major', 'Concomitant use of 5-HT3 receptor antagonists with agents that possess or enhance serotonergic activity such as antidepressants and lithium may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Treatment with 5-HT3 receptor antagonists has been associated with dose-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval including certain antidepressants and lithium may result in additive effects and increased risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system.', NULL, 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is warranted when initiating or increasing the dosages of these agents. In addition, the potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary. Moderately ill patients may also benefit from the administration of a serotonin antagonist (e.g., cyproheptadine, chlorpromazine). Severe cases should be managed under consultation with a toxicologist and may require sedation, neuromuscular paralysis, intubation, and mechanical ventilation in addition to the other measures. Due to the potential for additive effects on the QT interval, ECG monitoring may also be appropriate when 5-HT3 receptor antagonists are used with certain antidepressants or lithium. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83390/', '[1] "Product Information. Anzemet (dolasetron)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sancuso (granisetron)." ProStrakan Group, Bedminster, NJ.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc, Woodcliff Lake, NJ.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Aloxi (palonosetron)." MGI Pharma Inc, Minnetonka, MN.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83290, 'Donepezil', 'Desipramine', 'Moderate', 'Due to opposing effects, agents that possess anticholinergic activity (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; class IA antiarrhythmics especially disopyramide; carbamazepine; cimetidine; ranitidine) may negate the already small pharmacologic benefits of acetylcholinesterase inhibitors in the treatment of dementia. These agents may also adversely affect elderly patients in general. Clinically significant mental status changes associated with anticholinergic agents can range from mild cognitive impairment to delirium, and patients with Alzheimer''s disease and other dementia are especially sensitive.', NULL, 'Drugs that possess anticholinergic activity should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor.', NULL, 'Drugs that possess anticholinergic activity should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor. For patients requiring treatment to counteract adverse effects of acetylcholinesterase inhibitor therapy (e.g., gastrointestinal intolerance, urinary problems), an agent without anticholinergic properties should be used whenever possible. Otherwise, a dosage reduction, slower titration, or even discontinuation of the acetylcholinesterase inhibitor should be considered. For patients who are already receiving an acetylcholinesterase inhibitor with anticholinergic agents, every attempt should be made to discontinue the latter or substitute them with less anticholinergic alternatives.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83391/', '[1] Katz IR, Sands LP, Bilker W, DiFilippo S, Boyce A, D''Angelo K "Identification of medications that cause cognitive impairment in older people: the case of oxybutynin chloride." J Am Geriatr Soc 46 (1998): 8-13[2] Edwards KR, O''Connor JT "Risk of delirium with concomitant use of tolterodine and acetylcholinesterase inhibitors." J Am Geriatr Soc 50 (2002): 1165-6[3] Carnahan RM, Lund BC, Perry PJ, Chrischilles EA "The concurrent use of anticholinergics and cholinesterase inhibitors: rare event or common practice?" J Am Geriatr Soc 52 (2004): 2082-7[4] Fick DM, Cooper JW, Wade WE, Waller JL, Maclean JR, Beers MH "Updating the Beers criteria for potentially inappropriate medication use in older adults: results of a US consensus panel of experts." Arch Intern Med 163 (2003): 2716-2724[5] Beers MH, Ouslander JG, Rollingher I, Reuben DB, Brooks J, Beck JC "Explicit criteria for determining inappropriate medication use in nursing home residents." Arch Intern Med 151 (1991): 1825-32[6] Roe CM, Anderson MJ, Spivack B "Use of anticholinergic medications by older adults with dementia." J Am Geriatr Soc 50 (2002): 836-42', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83291, 'Dopamine', 'Desipramine', 'Major', 'Tricyclic antidepressants (TCAs) may markedly enhance the pressor response to parenteral direct-acting sympathomimetic agents and vasoconstrictor-containing local anesthetics.', NULL, 'Parenteral administration of direct-acting sympathomimetic agents should preferably be avoided during therapy with tricyclic antidepressants except in cases of emergency (e.', NULL, 'Parenteral administration of direct-acting sympathomimetic agents should preferably be avoided during therapy with tricyclic antidepressants except in cases of emergency (e.g., treatment of anaphylaxis). If concomitant use is necessary, initial dose and rate of administration of the sympathomimetic should be reduced, and cardiovascular status including blood pressure should be monitored closely. Although clinical data are lacking, it may be prudent to follow the same precaution with mixed-acting sympathomimetic agents.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83392/', '[1] Borg KO, Johnsson G, Jordo L, Lundborg P, Ronn O, Welin-Fogelberg I "Interaction studies between three antidepressant drugs (zimelidine, imipramine and chlorimipramine) and noradrenaline in healthy volunteers and some pharmacokinetics of the drugs studied." Acta Pharmacol Toxicol (Copenh) 45 (1979): 198-205[2] Ghose K "Sympathomimetic amines and tricyclic antidepressant drugs." Neuropharmacology 19 (1980): 1251-4[3] Fritz H, Hagstam KE, Lindqvist B "Local skin necrosis after intravenous infusion of norepinephrine, and the concept of endotoxinaemia. A clinical study on 10 cases." Acta Med Scand 178 (1965): 403