INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83420, 'Desipramine', 'Lithium carbonate', 'Moderate', 'The concomitant use of lithium and tricyclic antidepressants (TCAs) may potentiate the risk of neurotoxicity and psychotic symptoms, even at therapeutic serum lithium levels. The mechanism is unknown but may be related to synergistic effects on neurotransmission. Mania, myoclonus, tremors, memory difficulties, confusion, serotonin syndrome, and a neuroleptic malignant-like syndrome have been reported in patients treated with TCAs several days after the addition of lithium, in most cases requiring discontinuation of either the TCA or lithium. Elderly patients may be particularly susceptible to this interaction.', NULL, 'Close monitoring for symptoms of neurotoxicity is recommended if TCAs are used with lithium.', NULL, 'Close monitoring for symptoms of neurotoxicity is recommended if TCAs are used with lithium. Dosage adjustment or discontinuation of one or both drugs may be necessary if an interaction is suspected.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83521/', '[1] Kojima H, Terao T, Yoshimura R "Serotonin syndrome during clomipramine and lithium treatment." Am J Psychiatry 150 (1993): 1897[2] Devanand DP, Sackeim HA, Brown RP "Myoclonus during combined tricyclic antidepressant and lithium treatment." J Clin Psychopharmacol 8 (1988): 446-7[3] Delisle JD "Rapid switch in a bipolar patient during lithium-tricyclic therapy." Am J Psychiatry 143 (1986): 1326-7[4] Austin LS, Arana GW, Melvin JA "Toxicity resulting from lithium augmentation of antidepressant treatment in elderly patients." J Clin Psychiatry 51 (1990): 344-5[5] Rosenberg PB, Pearlman CA "NMS-like syndrome with a lithium-doxepin combination." J Clin Psychopharmacol 11 (1991): 75-6[6] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[7] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[8] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83421, 'Desipramine', 'Lofexidine', 'Moderate', 'The concomitant administration of tricyclic antidepressants may theoretically reduce the effects of lofexidine and increase central nervous system depressant effects. In addition, lofexidine has been associated with asymptomatic prolongation of the QT interval in some patients. Coadministration with other agents that could prolong the QT interval, such as tricyclic antidepressants, may theoretically result in an elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsades de pointes, because of additive arrhythmogenic potential related to their effects on cardiac conduction.', NULL, 'During concomitant use of these drugs, patients should be monitored for altered efficacy and excessive or prolonged CNS depression.', NULL, 'During concomitant use of these drugs, patients should be monitored for altered efficacy and excessive or prolonged CNS depression. Patients should be made aware of the possibility of additive effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and be cautioned about driving, operating machinery, or performing other hazardous tasks. The manufacturer recommends that the concurrent use of lofexidine with other medications that can prolong the QT interval be avoided. If no alternatives are available, patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83522/', '[1] "Product Information. Lucemyra (lofexidine)." US WorldMeds LLC, Louisville , KY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83422, 'Desipramine', 'Lomefloxacin', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', NULL, 'Caution and clinical monitoring are recommended.', NULL, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83523/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] Witchel HJ, Hancox JC, Nutt DJ "Psychotropic drugs, cardiac arrhythmia, and sudden death." J Clin Psychopharmacol 23 (2003): 58-77[3] Iannini PB "Cardiotoxicity of macrolides, ketolides and fluoroquinolones that prolong the QTc interval." Expert Opin Drug Saf 1 (2002): 121-8[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Ca