INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83445, 'Desipramine', 'Methohexital', 'Moderate', 'Tricyclic antidepressants may counteract the anticonvulsive effects of barbiturates by lowering the seizure threshold. Barbiturates may decrease the serum levels and effects of tricyclic antidepressants by induction of hepatic metabolism.', NULL, 'If the barbiturate is being taken for a seizure disorder, patients should be closely monitored for loss of seizure control.', NULL, 'If the barbiturate is being taken for a seizure disorder, patients should be closely monitored for loss of seizure control. Dose adjustments may be required. Monitoring for clinical evidence of additive toxicity and for clinical and/or laboratory evidence of reduced antidepressant effect is also advisable.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83546/', '[1] Garey KW, Amsden GW, Johns CA "Possible interaction between imipramine and butalbital." Pharmacotherapy 17 (1997): 1041-2[2] Burrows GD, Davies B "Antidepressants and barbiturates." Br Med J 4 (1971): 113[3] Silverman G, Braithwaite R "Interaction of benzodiazepines with tricyclic antidepressants." Br Med J 4 (1972): 111[4] Spina E, Avenoso A, Campo GM, Caputi AP, Perucca E "Phenobarbital induces the 2-hydroxylation of desipramine." Ther Drug Monit 18 (1996): 60-4[5] Crocker J, Morton B "Tricyclic (antidepressant) drug toxicity." Clin Toxicol 2 (1969): 397-402', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83446, 'Desipramine', 'Methotrimeprazine', 'Moderate', 'Coadministration of a phenothiazine with a tricyclic antidepressant (TCA) may result in elevated plasma concentrations of one or both drugs as well as additive adverse effects. Most phenothiazines and TCAs have been found to undergo metabolism by CYP450 2D6, thus competitive inhibition of the enzyme may occur when more than one of these agents are administered.', NULL, 'Concurrent use of phenothiazines and TCAs should be approached with caution, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, 'Concurrent use of phenothiazines and TCAs should be approached with caution, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication (e.g., abdominal pain, fever, heat intolerance, blurred vision, confusion, hallucinations) or cardiovascular toxicity (e.g., dizziness, palpitations, arrhythmias, syncope). Ambulatory patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83547/', '[1] Siris SG, Cooper TB, Rifkin AE, Brenner R, Lieberman JA "Plasma imipramine concentrat