INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83447, 'Desipramine', 'Methoxamine', 'Major', 'Tricyclic antidepressants (TCAs) may markedly enhance the pressor response to parenteral direct-acting sympathomimetic agents and vasoconstrictor-containing local anesthetics.', NULL, 'Parenteral administration of direct-acting sympathomimetic agents should preferably be avoided during therapy with tricyclic antidepressants except in cases of emergency (e.', NULL, 'Parenteral administration of direct-acting sympathomimetic agents should preferably be avoided during therapy with tricyclic antidepressants except in cases of emergency (e.g., treatment of anaphylaxis). If concomitant use is necessary, initial dose and rate of administration of the sympathomimetic should be reduced, and cardiovascular status including blood pressure should be monitored closely. Although clinical data are lacking, it may be prudent to follow the same precaution with mixed-acting sympathomimetic agents.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83548/', '[1] Borg KO, Johnsson G, Jordo L, Lundborg P, Ronn O, Welin-Fogelberg I "Interaction studies between three antidepressant drugs (zimelidine, imipramine and chlorimipramine) and noradrenaline in healthy volunteers and some pharmacokinetics of the drugs studied." Acta Pharmacol Toxicol (Copenh) 45 (1979): 198-205[2] Ghose K "Sympathomimetic amines and tricyclic antidepressant drugs." Neuropharmacology 19 (1980): 1251-4[3] Fritz H, Hagstam KE, Lindqvist B "Local skin necrosis after intravenous infusion of norepinephrine, and the concept of endotoxinaemia. A clinical study on 10 cases." Acta Med Scand 178 (1965): 