        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1373664, 'Aminophylline', 'Budesonide', 'Moderate', 'The concomitant use of theophylline and corticosteroids may theoretically increase the risk of hypokalemia due to additive potassium-lowering effects. Additionally, theophylline serum concentrations may be altered.', NULL, 'Monitoring for altered efficacy and safety of theophylline and altered serum potassium and theophylline concentrations is advisable when these drugs are coadministered.', NULL, 'Monitoring for altered efficacy and safety of theophylline and altered serum potassium and theophylline concentrations is advisable when these drugs are coadministered. Patients should be advised to notify their physician if they experience signs of hypokalemia (e.g., weakness, lethargy, and muscle pains or cramps), worsening respiratory symptoms, or signs of theophylline toxicity (e.g., nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
