    ) VALUES (1373770, 'Insulin human (inhalation, rapid acting)', 'Budesonide', 'Moderate', 'INTERVAL: Bronchodilators and other orally inhaled products may alter the absorption of inhaled human insulin.', NULL, 'Systemic administration or systemic absorption of orally inhaled corticosteroids and sympathomimetic amines (bronchodilators) may result in diminished efficacy of insulin.', NULL, 'Systemic administration or systemic absorption of orally inhaled corticosteroids and sympathomimetic amines (bronchodilators) may result in diminished efficacy of insulin. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
