INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83569, 'Desipramine', 'Ritodrine', 'Moderate', 'Concomitant use of beta-2 adrenergic agonists with tricyclic antidepressants (TCAs) may increase the risk and/or severity of cardiovascular adverse effects such as hypertension, palpitation, chest pain, and arrhythmia.', NULL, 'Caution is advised if beta-2 agonists are used with TCAs, or within two weeks of discontinuing the latter.', NULL, 'Caution is advised if beta-2 agonists are used with TCAs, or within two weeks of discontinuing the latter. Cardiovascular status should be closely monitored.', 'Synergism', 'Bremelanotide, Cabergoline, Black cohosh, Naproxen, Ibuprofen', 'Paroxetine, St. John''s Wort, Nefazodone, Oxitriptan, Viloxazine, Vortioxetine, Tryptophan, Vilazodone, Esketamine, Fluvoxamine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83670/', '[1] "Product Information. Arcapta Neohaler (indacaterol)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Maxair (pirbuterol)." 3M Pharmaceuticals, St. Paul, MN.[3] "Product Information. Foradil (formoterol)" Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Proventil (albuterol)." Schering Laboratories, Kenilworth, NJ.[5] "Product Information. Breo Ellipta (fluticasone-vilanterol)." GlaxoSmithKline, Research Triangle Park, NC.[6] "Product Information. Alupent (metaproterenol)." Boehringer-Ingelheim, Ridgefield, CT.[7] "Product Information. Xopenex (levalbuterol)." Sepracor, Marlborough, MA.[8] "Product Information. Brovana (arformoterol)." Sepracor Inc, Marlborough, MA.[9] "Product Information. Tornalate Metered Dose (bitolterol)." Dura Pharmaceuticals, San Diego, CA.