INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83585, 'Desipramine', 'Selpercatinib', 'Major', 'Selpercatinib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval. An electrocardiogram and serum electrolyte levels should be obtained prior to initiating selpercatinib therapy and periodically during treatment as appropriate based on individual risk factors including diarrhea. Correct hypokalemia, hypomagnesemia, and/or hypocalcemia before starting treatment and during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', 'Phenelzine, Duloxetine, Tranylcypromine, Desvenlafaxine, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, Bupropion, More', 'Dabrafenib, Acalabrutinib, Pexidartinib, Tepotinib, Avapritinib, Larotrectinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83686/', '[1] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83586, 'Desipramine', 'Sertraline', 'Major', 'Coadministration with sertraline may increase the plasma concentrations of some tricyclic antidepressants (TCAs). The proposed mechanism is sertraline inhibition of CYP450 2D6, the isoenzyme responsible for the metabolic clearance of many antidepressant and psychotropic drugs. Pharmacodynamically, the combination of sertraline (or any other selective serotonin reuptake inhibitor) and a TCA may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5HT1A receptors. The syndrome has been reported in a case involving sertraline and amitriptyline.', NULL, 'Caution is advised if sertraline (or other SSRIs) is prescribed with TCAs.', NULL, 'Caution is advised if sertraline (or other SSRIs) is prescribed with TCAs. Pharmacologic response and plasma TCA levels should be monitored more closely whenever sertraline is added to or withdrawn from therapy in patients stabilized on their existing antidepressant regimen, and the TCA dosage adjusted as necessary. Patients should be monitored closely for signs and symptoms of TCA toxicity (e.g., sedation, dry mouth, blurred vision, constipation, urinary retention) and/or excessive serotonergic activity (e.g., CNS irritability, altered consciousness, confusion, myoclonus, ataxia, abdominal cramping, hyperpyrexia, shivering, pupillary dilation, diaphoresis, hypertension, and tachycardia).', 'Metabolism, Synergism', 'Desipramine', 'Viloxazine, Sertraline, Esketamine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83687/', '[1] Sternbach H 