INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83613, 'Desipramine', 'Terbinafine', 'Moderate', 'Coadministration with terbinafine may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by terbinafine, which is expected to occur in patients who are CYP450 2D6 extensive metabolizers (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent).', NULL, 'Caution is advised if terbinafine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if terbinafine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever terbinafine is added to or withdrawn from therapy. Due to the long elimination half-life of terbinafine, especially following prolonged use, such interactions may be observed for several months after discontinuation of terbinafine therapy.', 'Metabolism', 'Salicylic acid, Selenium Sulfide, Thiabendazole, Miconazole, Nystatin, Ciclopirox, Griseofulvin, Econazole, Flucytosine, Tioconazole, Clotrimazole, Griseofulvin', 'Phenelzine, Tranylcypromine, Desvenlafaxine, Maprotiline, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Milnacipran, Tryptophan, Levomilnacipran, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83714/', '[1] Van der Kuy PH, Hooymans PM, Verkaaik AJ "Nortriptyline intoxication induced