INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83770, 'Desloratadine', 'Ketoconazole', 'Minor', 'Erythromycin and ketoconazole may increase the plasma concentrations of desloratadine and its metabolite. The probable mechanism is inhibition of CYP450 hepatic metabolism, although the isoenzymes responsible for desloratadine metabolism have not been identified. There were no clinically significant adverse effects associated with this interaction. Clinical monitoring of patient response and tolerance is recommended when erythromycin or ketoconazole are administered with desloratadine.', NULL, '-', NULL, '-', 'Metabolism', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, Doxylamine, More', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Trichlormethiazide, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83871/', '[1] "Product Information. Clarinex (desloratadine)." Schering Corporation, Kenilworth, NJ.', 1767369485);
