INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (83975, 'Desvenlafaxine', 'Linezolid', 'Major', 'Coadministration of linezolid with serotonergic agents may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Linezolid is a reversible, nonselective monoamine oxidase inhibitor (MAOI). As such, it can enhance serotonergic effects by inhibiting serotonin metabolism.', NULL, 'Concomitant use of linezolid and serotonergic agents should be avoided unless no alternatives exist and close monitoring for the development of serotonin syndrome can be provided.', NULL, 'Concomitant use of linezolid and serotonergic agents should be avoided unless no alternatives exist and close monitoring for the development of serotonin syndrome can be provided. In general, most serotonergic psychiatric drugs should be stopped 1 to 2 weeks (i.e., 4 to 5 half-lives) prior to treatment with an MAOI, while others such as fluoxetine may require discontinuation up to 5 weeks in advance due to its prolonged half-life. Treatment with serotonergic medications may be resumed 24 hours after the last dose of linezolid.', 'Synergism', 'Tinidazole, Bacitracin, Polymyxin B, Oritavancin, Daptomycin, Vancomycin, Metronidazole, Telavancin, Methenamine, Spectinomycin, Lefamulin, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/84076/', '[1] Boyer EW, Shannon M "The serotonin syndrome." N Engl J Med 352 (2005): 1112-20[2] "Product Information. Zyvox (linezolid)" Pharmacia and Upjohn, Kalamazoo, MI.[3] Mills KC "Serotonin syndrome: A clinical update." Crit Care Clin 13 (199