INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (84246, 'Fluoxetine', 'Deutetrabenazine', 'Major', 'Deutetrabenazine has been associated with modest QT prolongation. Coadministration with a strong CYP450 2D6 inhibitor may increase the plasma concentrations of the pharmacologically active dihydro-metabolites of deutetrabenazine, alfa- and beta-HTBZ (dihydrotetrabenazine), which may increase the risk of adverse effects, including somnolence, clinically relevant QT interval prolongation, parkinsonism, akathisia, neuroleptic malignant syndrome, depression, and suicidality.', NULL, 'Pharmacologic response to deutetrabenazine should be monitored more closely whenever a strong CYP450 2D6 inhibitor (e.', NULL, 'Pharmacologic response to deutetrabenazine should be monitored more closely whenever a strong CYP450 2D6 inhibitor (e.g.,fluoxetine, paroxetine, or quinidine) is added to or withdrawn from therapy. Deutetrabenazine is not recommended for use in patients with congenital long QT syndrome or with arrhythmias associated with a prolonged QT interval. Assessment of baseline QT interval and periodic monitoring during therapy may be considered. The maximum recommended dosage of deutetrabenazine is 18 mg per dose and 36 mg per day during coadministration with a potent CYP450 2D6 inhibitor or in patients who are poor metabolizers of CYP450 2D6. Patients and their caregivers should be advised to notify their physician if they experience new or worsening depression, suicidal thoughts, parkinsonism, restlessness, agitation, dysphagia, and/or excessive sedation while taking deutetrabenazine. Patients should also be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/84347/', '[1] "Product Information. Austedo (deutetrabenazine)." Teva Pharmaceuticals USA, North Wales, PA.[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (84247, 'Fluphenazine', 'Deutetrabenazine', 'Major', 'Tetrabenazine causes central dopamine depletion by binding reversibly to human vesicular monoamine transporter type 2 (VMAT2) and interfering with presynaptic monoamine storage mechanisms. Coadministration of tetrabenazine in combination with neuroleptic agents or other dopamine antagonists (e.g., metoclopramide) may result in severe manifestations of dopamine deficiency. Deutetrabenazine, as well as many neuroleptic agents (e.g., asenapine, clozapine, droperidol, haloperidol, iloperidone, paliperidone, pimozide, phenothiazines, quetiapine, risperidone, sertindole, ziprasidone) and other dopamine antagonists (e.g., domperidone) have been associated with dose-related prolongation of the QT interval. Theoretically, the use of these agents in combination may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential related to their effects on cardiac conduction.', NULL, 'The use of deutetrabenazine in combination with neuroleptic agents or other dopamine antagonists should preferable be avoided.', NULL, 'The use of deutetrabenazine in combination with neuroleptic agents or other dopamine antagonists should preferable be avoided. When coadministration is required, patients should be instructed to notify their physician if they experience extrapyramidal symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia. Clinicians, caregivers, and family members should be apprised of the risk of neuroleptic malignant syndrome and be alert to potential signs and symptoms such as mental status changes (e.g. mutism, catatonia, stupor, coma, agitation, confusion, hallucinations, delusions), autonomic instability, restlessness, rigidity, ataxia, myoclonus, hyperreflexia, tremors, diaphoresis, elevated creatine phosphokinase levels, and hyperpyrexia. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/84348/', '[1] "Product Information. Austedo (deutetrabenazine)." Teva Pharmaceuticals USA, North Wales, PA.', 1767365205);
