INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (84382, 'Deutetrabenazine', 'Pimavanserin', 'Moderate', 'Deutetrabenazine has been associated with modest QT interval prolongation. In addition, central nervous system (CNS)-depressant effects may be additively or synergistically increased in patients taking deutetrabenazine with certain other drugs that cause these effects, especially in elderly or debilitated patients.', NULL, 'Coadministration of deutetrabenazine with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of deutetrabenazine with other drugs that can prolong the QT interval should generally be avoided. Deutetrabenazine is not recommended for use in patients with congenital long QT syndrome or with arrhythmias associated with a prolonged QT interval. If concomitant use is required, assessment of baseline QT interval and periodic monitoring during therapy may be considered. In addition, dosage reduction may be considered in patients on concurrent therapy with a strong CYP450 2D6 inhibitor, or in patients who are poor metabolizers of CYP450 2D6.', 'Metabolism, Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/84483/', '[1] "Product Information. Austedo (deutetrabenazine)." Teva Pharmaceuticals USA, North Wales, PA.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (84383, 'Deutetrabenazine', 'Pimozide', 'Major', 'Pimozide can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with pimozide. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', NULL, 'Coadministration of pimozide with other drugs that can prolong the QT interval is considered contraindicated.', NULL, 'Coadministration of pimozide with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/84484/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Orap Tablets (pimozide)." Gate Pharmaceuticals, Sellersville, PA.[3] Glassman AH, Bigger JT Jr "Antipsychotic drugs: prolonged QTc interval, torsade de pointes, and sudden death." Am J Psychiatry 158 (2001): 1774-82[4] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_li