INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (84469, 'Deutetrabenazine', 'Triclabendazole', 'Moderate', 'Deutetrabenazine has been associated with modest QT interval prolongation. In addition, central nervous system (CNS)-depressant effects may be additively or synergistically increased in patients taking deutetrabenazine with certain other drugs that cause these effects, especially in elderly or debilitated patients.', NULL, 'Coadministration of deutetrabenazine with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of deutetrabenazine with other drugs that can prolong the QT interval should generally be avoided. Deutetrabenazine is not recommended for use in patients with congenital long QT syndrome or with arrhythmias associated with a prolonged QT interval. If concomitant use is required, assessment of baseline QT interval and periodic monitoring during therapy may be considered. In addition, dosage reduction may be considered in patients on concurrent therapy with a strong CYP450 2D6 inhibitor, or in patients who are poor metabolizers of CYP450 2D6.', 'Metabolism, Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/84570/', '[1] "Product Information. Austedo (deutetrabenazine)." Teva Pharmaceuticals USA, North Wales, PA.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (84470, 'Deutetrabenazine', 'Trifluoperazine', 'Major', 'Tetrabenazine causes central dopamine depletion by binding reversibly to human vesicular monoamine transporter type 2 (VMAT2) and interfering with presynaptic monoamine storage mechanisms. Coadministration of tetrabenazine in combination with neuroleptic agents or other dopamine antagonists (e.g., metoclopramide) may result in severe manifestations of dopamine deficiency. Deutetrabenazine, as well as many neuroleptic agents (e.g., asenapine, clozapine, droperidol, haloperidol, iloperidone, paliperidone, pimozide, phenothiazines, quetiapine, risperidone, sertindole, ziprasidone) and other dopamine antagonists (e.g., domperidone) have been associated with dose-related prolongation of the QT interval. Theoretically, the use of these agents in combination may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential related to their effects on cardiac conduction.', NULL, 'The use of deutetrabenazine in combination with neuroleptic agents or other dopamine antagonists should preferable be avoided.', NULL, 'The use of deutetrabenazine in combination with neuroleptic agents or other dopamine antagonists should preferable be avoided. When coadministration is required, patients should be instructed to notify their physician if they experience extrapyramidal symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia. Clinicians, caregivers, and family members should be apprised of the risk of neuroleptic malignant syndrome and be alert to potential signs and symptoms such as mental status changes (e.g. mutism, catatonia, stupor, coma, agitation, confusion, hallucinations, delusions), autonomic instability, restlessness, rigidity, ataxia, myoclonus, hyperreflexia, tremors, diaphoresis, elevated creatine phosphokinase levels, and hyperpyrexia. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/84571/', '[1] "Product Information. Austedo (deutetrabenazine)." Teva Pharmaceuticals USA, North Wales, PA.', 1767365205);
