INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1374163, 'Cefaclor', 'Bumetanide', 'Moderate', 'Limited data suggest that furosemide and possibly other loop diuretics may potentiate the nephrotoxicity of some cephalosporins. The exact mechanism of interaction is unknown, although furosemide has been shown to increase the plasma concentrations and/or reduce the clearance of several cephalosporins such as cephaloridine and ceftazidime.', 'DDInter', 1767317475, 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins. Renal function should be monitored, particularly when high dosages are used or when these medications are administered in the elderly or patients with preexisting renal impairment.', 'Synergism', 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins.', NULL, NULL, 'Moderate', 'pharmacodynamic');
