INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1374298, 'Irinotecan', 'Bumetanide', 'Moderate', 'Irinotecan-induced vomiting or diarrhea may increase the risk and/or severity of dehydration produced by diuretics.', 'DDInter', 1767317475, 'Concomitant use of diuretics during irinotecan administration or during periods of vomiting or diarrhea that may follow is not recommended.', 'Others', 'Concomitant use of diuretics during irinotecan administration or during periods of vomiting or diarrhea that may follow is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
