INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1374369, 'Bumetanide', 'Norfloxacin', 'Minor', 'The plasma concentration of lomefloxacin following administration of furosemide has been reported to be higher than after lomefloxacin alone. The area under the curve of lomefloxacin was increased and the total and renal clearances of lomefloxacin were decreased. A similar interaction may occur if other quinolones are administered with other loop diuretics. The loop diuretic may decrease the renal clearance of the quinolone by competing for tubular secretion.', 'DDInter', 1767317475, '-', 'Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
