INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (84531, 'Dexamethasone (ophthalmic)', 'Miglitol', 'Minor', 'The efficacy of insulin and other antidiabetic agents may be diminished by concomitant use of ophthalmic corticosteroids, particularly during prolonged use. Corticosteroids can raise blood glucose levels by antagonizing the action and suppressing the secretion of insulin, which results in inhibition of peripheral glucose uptake and increased gluconeogenesis. Corticosteroids may interfere with blood glucose control and may cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes. Systemic absorption from use of ophthalmic corticosteroids may occur. Clinical monitoring of glycemic control is recommended if ophthalmic corticosteroids are administered to diabetic patients.', NULL, '-', NULL, '-', 'Antagonism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/84632/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[3] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[4] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[5] "Position Statement: evidence-based nutrition principles and recommendations for the treatment and prevention of diabetes related complications. American Diabetes Association." Diabetes Care 25(Suppl 1) (2002): S50-S60[6] "Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas Cit