INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (84711, 'Dexlansoprazole', 'Rilpivirine', 'Major', 'Coadministration with proton pump inhibitors may significantly decrease the oral bioavailability of rilpivirine. The proposed mechanism involves a pH-dependent reduction in dissolution and absorption of rilpivirine, which is poorly soluble over a wide pH range.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of rilpivirine with proton pump inhibitors is considered contraindicated.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of rilpivirine with proton pump inhibitors is considered contraindicated.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/84812/', '[1] "Product Information. Edurant (rilpivirine)." Tibotec Pharmaceuticals, Titusville, NJ.[2] "Product Information. Edurant (rilpivirine)." Tibotec Pharmaceuticals, Titusville, NJ.[3] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[4] "Product Information. Viread (tenofovir)." Gilead Sciences, Foster City, CA.', 1767363022);
