INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (170191, 'Teriflunomide', 'St. John''s Wort', 'Moderate', 'Coadministration of teriflunomide with known strong inducers of CYP450 isoenzymes and efflux transporters, such as phenobarbital and St. John''s wort, may decrease the plasma concentrations and therapeutic effects of teriflunomide. The proposed mechanism is induction of CYP450 isoenzymes and/or efflux transporters responsible for the metabolic clearance of teriflunomide.', NULL, 'Caution and close clinical and laboratory monitoring are recommended if teriflunomide is coadministered with strong inducers of CYP450 isoenzymes and/or efflux transporters.', NULL, 'Caution and close clinical and laboratory monitoring are recommended if teriflunomide is coadministered with strong inducers of CYP450 isoenzymes and/or efflux transporters. Dosage adjustments may be required whenever a strong inducer is added to or withdrawn from therapy.', 'Metabolism', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, Vortioxetine, Levomilnacipran, Doxepin, Isocarboxazid, Viloxazine, Amoxapine, Esketamine, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170367/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767369485);
