INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (170400, 'Trifluoperazine', 'Tetrabenazine', 'Major', 'Tetrabenazine causes central dopamine depletion by binding reversibly to human vesicular monoamine transporter type 2 (VMAT2) and interfering with presynaptic monoamine storage mechanisms. Coadministration of tetrabenazine in combination with neuroleptic agents or other dopamine antagonists (e.g., metoclopramide, amisulpride) may result in severe manifestations of dopamine deficiency. Tetrabenazine as well as many neuroleptic agents (e.g., asenapine, clozapine, droperidol, haloperidol, iloperidone, paliperidone, pimozide, phenothiazines, quetiapine, risperidone, sertindole, ziprasidone) and other dopamine antagonists (e.g., domperidone, amisulpride) have been associated with dose-related prolongation of the QT interval.', NULL, 'The use of tetrabenazine in combination with neuroleptic agents or other dopamine antagonists should preferably be avoided.', NULL, 'The use of tetrabenazine in combination with neuroleptic agents or other dopamine antagonists should preferably be avoided. When coadministration is required, patients should be instructed to notify their physician if they experience extrapyramidal symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia. Clinicians, caregivers, and family members should be apprised of the risk of neuroleptic malignant syndrome and be alert to potential signs and symptoms such as mental status changes (e.g., mutism, catatonia, stupor, coma, agitation, confusion, hallucinations, delusions), autonomic instability, restlessness, rigidity, ataxia, myoclonus, hyperreflexia, tremors, diaphoresis, elevated creatine phosphokinase levels, and hyperpyrexi