INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (170634, 'Tocilizumab', 'Thalidomide', 'Moderate', 'Thalidomide can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk and/or severity of nerve damage. Peripheral neuropathy is a common, potentially severe side effect of thalidomide that may be irreversible.', NULL, 'Caution is advised if thalidomide is used in combination with other neurotoxic agents.', NULL, 'Caution is advised if thalidomide is used in combination with other neurotoxic agents. All patients treated with thalidomide should be examined at monthly intervals for the first three months of therapy and periodically thereafter to detect early signs of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Electrophysiological testing may be performed at baseline and every six months during therapy to detect asymptomatic neuropathy. Consideration should be given to immediate discontinuation of thalidomide in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Ravulizumab, Mycophenolic acid, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, Sutimlimab, More', 'Antithymocyte immunoglobulin (rabbit), Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Thalidomide, Belimumab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170820/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 6