INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (85003, 'Dexmethylphenidate', 'Midodrine', 'Moderate', 'The pressor effects of midodrine may be potentiated by the concomitant use of other agents that cause vasoconstriction, including sympathomimetics and ergot alkaloids. Midodrine is an alpha-adrenergic agonist and may cause marked elevation of supine arterial blood pressure, or supine hypertension.', NULL, 'Caution is advised if midodrine must be used in combination with other agents that can increase blood pressure.', NULL, 'Caution is advised if midodrine must be used in combination with other agents that can increase blood pressure. Supine and sitting blood pressures should be closely monitored. Supine hypertension can often be controlled by keeping the patient from being fully supine, for example, sleeping with the head of the bed elevated. Taking the last daily dose of midodrine 3 to 4 hours before bedtime may also help to minimize nighttime supine hypertension. Patients should be advised to contact their physician immediately if they experience symptoms of supine hypertension such as cardiac awareness, pounding in the ears, headache, and blurred vision. Midodrine should be discontinued if supine hypertension persists.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85104/', '[1] "Product Information. ProAmatine (midodrine)." Roberts Pharmaceutical Corporation, Eatontown, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (85004, 'Dexmethylphenidate', 'Modafinil', 'Minor', 'In single-dose pharmacokinetic studies, healthy volunteers given modafinil (200 mg) with dextroamphetamine (10 mg) or methylphenidate (40 mg) exhibited a delay in the absorption of modafinil by approximately 1 hour. The mechanism of interaction is unknown. No other alterations in the pharmacokinetics of any of the drugs were observed. Although not studied, the interaction is likely to also occur with the various prodrug, racemic, and enantiomeric formulations of these drugs (e.g., amphetamine, lisdexamfetamine, dexmethylphenidate, armodafinil).', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85105/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[3] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.[4] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol interactions in humans." Clin Pharmacol Ther 57 (1995): 559-68[5] Jiao X, Velez S, Ringstad J, Eyma V, Miller D, Bleiberg M "Myocardial infarction associated with Adderall XR and alcohol use in a young man." J Am Board Fam Med 22 (2009): 197-201[6] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[7] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.', 1767363022);
