INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (85040, 'Dexmethylphenidate', 'Vortioxetine', 'Moderate', 'Coadministration with methylphenidate may increase the plasma concentrations and effects of selective serotonin reuptake inhibitors (SSRIs). Human pharmacologic studies have shown that methylphenidate may inhibit the metabolism of some antidepressants including SSRIs. Nevertheless, the combination has been used therapeutically to improve clinical response in the treatment of attention-deficit hyperactivity disorder and to augment the effects of SSRIs in the treatment of depression.', NULL, 'Pharmacologic response to SSRIs should be monitored more closely whenever methylphenidate (racemic) or dexmethylphenidate (the more pharmacologically active d-enantiomer) is added to or withdrawn from therapy, and the dosage of one or both drugs adjusted as necessary.', NULL, 'Pharmacologic response to SSRIs should be monitored more closely whenever methylphenidate (racemic) or dexmethylphenidate (the more pharmacologically active d-enantiomer) is added to or withdrawn from therapy, and the dosage of one or both drugs adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85141/', '[1] Stoll AL, Pillay SS, Diamond L, Workum SB, Cole JO "Methylphenidate augmentation of serotonin selective reuptake inhibitors: a case series." J Clin Psychiatry 57 (1996): 72-6[2] Findling RL "Open-label treatment of comorbid depression and attentional disorders with co-administration of serotonin reuptake inhibitors and psychostimulants in children, adolescents, and adults: a case series." J Child Adolesc Psychopharmacol 6 (1996): 165-75[3] Lavr