INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (85043, 'Bedaquiline', 'Promethazine', 'Major', 'Bedaquiline can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85144/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (85044, 'Bendroflumethiazide', 'Promethazine', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85145/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Risperdal (risperidone)." Janssen Pharmaceu