INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (85195, 'Flumazenil', 'Diazepam', 'Moderate', 'The administration of flumazenil can result in convulsions in patients who have been receiving long-term treatment with benzodiazepines for epilepsy or sedation or who have recently received repeated doses of parenteral benzodiazepines. The mechanism is abrupt reversal of the benzodiazepine''s anticonvulsive effects. In addition, withdrawal seizures may occur in patients who are physically dependent on benzodiazepines.', NULL, 'The use of flumazenil is not recommended in patients with epilepsy who have been receiving long-term treatment with benzodiazepines.', NULL, 'The use of flumazenil is not recommended in patients with epilepsy who have been receiving long-term treatment with benzodiazepines. In addition, flumazenil is contraindicated in patients who have received a benzodiazepine to control potentially life-threatening conditions such as status epilepticus or increased intracranial pressure. Extreme caution, dose individualization, and close monitoring is recommended for other patients. Clinicians should be prepared to manage convulsions. Most seizures have been successfully managed with benzodiazepines, phenytoin, or barbiturates.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85296/', '[1] "Product Information. Romazicon (flumazenil)." Roche Laboratories, Nutley, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (85196, 'Fluoxetine', 'Diazepam', 'Moderate', 'Several studies have suggested that the effects of benzodiazepines, particularly their effects on psychomotor performance, may be increased by fluoxetine. The mechanism may be related to additive CNS depressant effects and/or inhibition of CYP450 2C19 and/or 3A4 benzodiazepine metabolism by fluoxetine. The clinical implications are uncertain, but effects may be more pronounced in the elderly.', NULL, 'Patients should be monitored for excessive CNS depression and psychomotor impairment.', NULL, 'Patients should be monitored for excessive CNS depression and psychomotor impairment. Dose reductions may be required. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness until they know how these agents affect them. Patients should also be advised to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Metabolism, Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85297/', '[1] Lemberger L, Rowe H, Bosomworth JC, Tenbarge JB, Bergstrom RF "The effect of fluoxetine on the pharmacokinetics and psychomotor responses of diazepam." Clin Pharmacol Ther 43 (1988): 412-9[2] Dent LA, Orrock MW "Warfarin-fluoxetine and diazepam-fluoxetine interaction." Pharmacotherapy 17 (1997): 170-2[3] Moskowitz H, Burns M "The effects on performance of two antidepressants, alone and in combination with diazepam." Prog Neuropsychopharmacol Biol Psychiatry 12 (1