INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (85464, 'Fenoldopam', 'Diazoxide', 'Moderate', 'The use of fenoldopam with other hypotensive agents has not been studied. However, these combinations would theoretically increase the risk of severe symptomatic hypotension.', NULL, 'Caution is strongly advised during concurrent use.', NULL, 'Caution is strongly advised during concurrent use. Inpatient monitoring of the heart rate and blood pressure is recommended. Sublingual nitroglycerin has been used safely with fenoldopam.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85565/', '[1] "Product Information. Corlopam (fenoldopam)." Neurex, Menlo Park, CA.[2] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (85465, 'Fludrocortisone', 'Diazoxide', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity. Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', NULL, 'Patients on prolonged (i.', NULL, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure. The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85566/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (85466, 'Fluphenazine', 'Diazoxide', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85567/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.[5] "Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb, Princeton, NJ.[6] Markowitz JS, Wells BG, Carson WH "Interactions between antipsychotic and antihypertensive drugs." Ann Pharmacother 29 (1995): 603-9[7] Aronowitz JS, Chakos MH, Safferman AZ, Lieberman JA "Syncope associated with the combination of clozapine and enalapril." J Clin Psychopharmacol 14 (1994): 429-30[8] "Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company, Indianapolis, IN.[9] Fruncillo R, Gibbons W, Vlasses P, Ferguson R "Severe hypotension associated with concurrent clonidine and antipsychotic medication." Am J Psychiatry 142 (1985): 274[10] "Product Information. Geodon (ziprasidone)." Pfizer US Pharmaceuticals, New York, NY.[11] White WB "Hypotension with postural syncope secondary to the combination of chlorpromazine and captopril." Arch Intern Med 146 (1986): 1833-4', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (85467, 'Flurazepam', 'Diazoxide', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85568/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004): 2463-75[5] Cerner Multum, Inc. "Australian Product Information.