        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1374875, 'Buprenorphine', 'Ozanimod', 'Major', 'Due to the risk of bradycardia and atrioventricular (AV) block, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of ozanimod treatment in patients receiving drugs that prolong the QT interval.', NULL, 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, treatment with ozanimod should generally not be initiated in patients who are concurrently treated with QT prolonging drugs with known arrhythmogenic properties.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
