INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (85606, 'Dorzolamide (ophthalmic)', 'Diclofenamide', 'Moderate', 'Following ophthalmic administration, brinzolamide and dorzolamide are systemically absorbed and may have additive pharmacologic effects with other, systemically administered carbonic anhydrase inhibitors. The risk of adverse effects such as drowsiness, paresthesia, tinnitus, electrolyte imbalance, metabolic acidosis, and gastrointestinal disturbances may be increased.', NULL, 'The concomitant use of ocular and systemic carbonic anhydrase inhibitors is not recommended.', NULL, 'The concomitant use of ocular and systemic carbonic anhydrase inhibitors is not recommended.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85707/', '[1] "Product Information. Azopt (brinzolamide ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Trusopt (dorzolamide ophthalmic)." Merck & Co, Inc, West Point, PA.', 1767369485);
