INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (85684, 'Diclofenamide', 'Nefazodone', 'Moderate', 'Nefazodone may potentiate the hypotensive effect of many drugs including vasodilators and antihypertensive agents. Orthostatic hypotension may occur.', NULL, 'Caution and close monitoring for altered efficacy and safety are recommended if patients receive nefazodone with agents that can produce significant hypotension such as antihypertensive agents or vasodilators.', NULL, 'Caution and close monitoring for altered efficacy and safety are recommended if patients receive nefazodone with agents that can produce significant hypotension such as antihypertensive agents or vasodilators. Patients should be made aware of the possible side effects (e.g., dizziness, lightheadedness, orthostasis) and be cautioned about driving, operating machinery, or performing other hazardous tasks.', 'Synergism', 'Tinidazole, Ascorbic acid, Sulfadiazine, Lactic acid, Amphotericin B, Ciclopirox, Darunavir, Probenecid, Tioconazole, Brinzolamide, Clotrimazole, More', 'Maprotiline, St. John''s Wort, Doxepin, Isocarboxazid, Viloxazine, Amoxapine, Imipramine, Esketamine, Phenelzine, Desipramine, Tranylcypromine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85785/', '[1] "Product Information. Serzone (nefazodone)." Bristol-Myers Squibb, Princeton, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeu